00540                 SolidTextures.DrawTexture(new Rect(fieldL, line, fieldw, BCL_HEIGHT - SPACING), SolidTextures.darken052D);
00541 
00542             DrawBCLField(BitCullingLevel.DropAll, fieldL, fldWdth4th, fldWdth8th, fldWdth16th);
00543             DrawBCLField(BitCullingLevel.DropHalf, fieldL, fldWdth4th, fldWdth8th, fldWdth16th);
00544             DrawBCLField(BitCullingLevel.DropThird, fieldL, fldWdth4th, fldWdth8th, fldWdth16th);
00545             DrawBCLField(BitCullingLevel.NoCulling, fieldL, fldWdth4th, fldWdth8th, fldWdth16th);
00546 
00547             line += LINEHEIGHT;
00548             if (!fc.expandBCL)
00549                 return;
00550 
00551             EditorGUI.LabelField(new Rect(paddedleft, line, labelw, LINEHEIGHT), "bits", MINI_LBL_STYLE);
00552 
00553             // Reset Button
00554             if (GUI.Button(new Rect(fieldL - 18 - 2, line, 18, LINEHEIGHT), rstLabel, (GUIStyle)"minibuttonleft"))
00555             {
00556                 haschanged = true;
00557                 Undo.RecordObject(p.serializedObject.targetObject, "Reset BCL To Default");
00558                 fc.SetBits(fc.Bits);
00559             }
00560 
00561             line += LINEHEIGHT;
00562             EditorGUI.LabelField(new Rect(paddedleft, line, labelw, LINEHEIGHT), "zones", MINI_LBL_STYLE);
00563             line += LINEHEIGHT;
00564 
