00568         private void DrawBCLField(BitCullingLevel bcl, float fieldL, float fldWdth4th, float fldWdth8th, float fldWdth16th)
00569         {
00570             int holdindent = EditorGUI.indentLevel;
00571             EditorGUI.indentLevel = 0;
00572 
00573             float lines = line;
00574 
00575             float left = fieldL + fldWdth4th * (3 - (int)bcl);
00576 
00577             if (fc.expandBCL)
00578             {
00579                 if (bcl == BitCullingLevel.DropAll || bcl == BitCullingLevel.DropThird)
00580                     SolidTextures.DrawTexture(new Rect(left, line, fldWdth4th, BCL_HEIGHT), SolidTextures.darken052D);
00581 
00582                 string clvl =
00583                 r.width > 300 ?
00584                 (((int)bcl == 0) ? "Cull None" : ((int)bcl == 1) ? "Lvl 1" : ((int)bcl == 2) ? "Lvl 2" : "Cull All") :
00585                 (((int)bcl == 0) ? "None" : ((int)bcl == 1) ? "Lvl 1" : ((int)bcl == 2) ? "Lvl 2" : "All");
00586 
00587                 EditorGUI.LabelField(new Rect(left, lines, fldWdth4th, LINEHEIGHT), clvl, (GUIStyle)"ProjectBrowserGridLabel");
00588                 lines += LINEHEIGHT;
00589             }
00590 
00591             EditorGUI.indentLevel = 0;
00592             int bits = EditorGUI.DelayedIntField(new Rect(left + fldWdth16th, lines + 1, fldWdth8th, LINEHEIGHT), fc.GetBits(bcl), MINI_TXT_FLD_STYLE);
00593             EditorGUI.indentLevel = 0;
00594 
00595             if (bits != fc.GetBits(bcl))
00596             {
00597                 haschanged = true;
00598                 Undo.RecordObject(p.serializedObject.targetObject, "BCL " + bcl + " changed");
00599                 fc.SetBits(bits, bcl);
00600 
00601                 Debug.Log("bcl " + bcl + " " + bits + " " + fc.GetBits(bcl));
00602             }
00603 
00604             if (fc.expandBCL)
00605             {
00606                 lines += LINEHEIGHT;
00607                 var rng = (double)Math.Abs(fc.Max - fc.Min);
00608                 string str = ((fc.GetBits(bcl) == 0) ? 0 : (rng / Math.Pow(2, fc.Bits - fc.GetBits(bcl)))).ToString("####0.#####");
00609                 EditorGUI.LabelField(new Rect(left, lines, fldWdth4th, LINEHEIGHT), str, (GUIStyle)"ProjectBrowserGridLabel");
00610             }
00611 
00612             EditorGUI.indentLevel = holdindent;
00613         }
