00615         private float CalculateHeight(FloatCrusher fc, int hash)
00616         {
00617             
00618 
00619             bool noSettings = (fc.BitsDeterminedBy == BitsDeterminedBy.HalfFloat || fc.BitsDeterminedBy == BitsDeterminedBy.Uncompressed || fc.BitsDeterminedBy == BitsDeterminedBy.Disabled);
00620             bool noRange = fc.TRSType == TRSType.Normal;
00621 
00622             float bclLine = (fc.expandBCL) ? BCL_HEIGHT : (fc.showBCL) ? LINEHEIGHT : 0;
00623 
00624             float settingsLen = (noSettings) ? 0 : (noRange ? 0 : SETTINGS_HGHT) + ACCCNTR_HGHT + ACTUAL_HGHT + bclLine;
00625 
00626             return PADDING + HEADR_HGHT + PADDING +
00627                 (fc.Enabled && fc.expanded ?
00628                 (COMPMTHD_HGHT + ((noSettings) ? 0 : settingsLen)) :
00629                 0);
00630         }
