00633         public override float GetPropertyHeight(SerializedProperty property, GUIContent label)
00634         {
00635             // Hackjob way to get the target - needs to reference a serialized field in order to work.
00636             fc = (FloatCrusher)DrawerUtils.GetParent(property.FindPropertyRelative("_min"));
00637             int hash = fc.GetHashCode();
00638             return CalculateHeight(fc, hash);
00639         }
