00161 //      private static T RecurseLayer<T>(List<Transform> currList, List<Transform> nextList) where T : Component
00162 //      {
00163 
00164 //          nextList.Clear();
00165 
00166 //          /// Iterate each transform in our current depth list
00167 //          int count = currList.Count;
00168 //          for (int i = 0; i < count; ++i)
00169 //          {
00170 //              var t = currList[i];
00171 
00172 //              /// Check each child for end of nesting, seeked component, and if not found add to next layer list
00173 //              int ccnt = t.childCount;
00174 //              for (int c = 0; c < ccnt; ++c)
00175 //              {
00176 //                  var child = t.GetChild(c);
00177 
00178 //                  if (child.GetComponent<NetObject>())
00179 //                      continue;
00180 
00181 //                  var foundchild = child.GetComponent<T>();
00182 
00183 //                  /// If we found the component, we are done
00184 //                  if (foundchild)
00185 //                      return foundchild;
00186 
00187 //                  nextList.Add(child);
00188 //              }
00189 //          }
00190 
00191 //          /// If nothing was found and there are no more layers left, return null
00192 //          if (nextList.Count == 0)
00193 //              return null;
00194 //          /// Else more layers were found, go the next layer deep.
00195 //          else
00196 //              /// Swap next and curr lists for the next layer
00197 //              return RecurseLayer<T>(nextList, currList);
