00398         public static void OverlayHeader(string HelpURL,
00399             Texture2D backTexture, Texture2D gridTexture, Texture2D iconTexture, Texture2D textTexture, Texture2D leftCap, bool thin = false)
00400         {
00401             int h = thin ? 16 : 24;
00402             Rect r = EditorGUILayout.GetControlRect(true, thin ? h : (h + 2));
00403 
00404             float left = r.xMin;
00405 
00406             if (!thin)
00407                 r.yMin += 2;
00408 
00409             r.xMin = thin ? r.xMin + EditorGUIUtility.labelWidth  /*+ 2*/ : 32;
00410 
00411             string url = HelpURL;
00412             if (url != null)
00413             {
00414                 EditorGUIUtility.AddCursorRect(r, MouseCursor.Link);
00415                 if (GUI.Button(r, GUIContent.none, GUIStyle.none))
00416                     Application.OpenURL(url);
00417             }
00418 
00419             var hr = new Rect(r) /*{ xMin = r.xMin + 8 }*/;
00420 
00421             if (backTexture != null)
00422                 GUI.DrawTexture(hr, backTexture);
00423 
00424 
00426             if (thin)
00427             {
00428                 // GUI.DrawTexture(new Rect(hr) { xMin = hr.xMin - 2, width = 2 }, leftCapThin);
00429                 // for thin, add an extra bit of lower padding. Don't add an end cap.
00430                 EditorGUILayout.GetControlRect(false, 2);
00431             }
00432             else
00433                 GUI.DrawTexture(new Rect(r) { xMin = r.xMin - 16, width = 16 }, leftCap);
00434 
00436             if (gridTexture != null)
00437                 GUI.DrawTexture(hr, gridTexture, ScaleMode.ScaleAndCrop);
00438 
00440             if (iconTexture != null)
00441                 GUI.DrawTexture(new Rect(hr.xMax - 248, hr.yMin, 248, h), iconTexture);
00442 
00444             if (textTexture != null)
00445                 GUI.DrawTexture(new Rect(hr) { x = thin ? hr.x + 6 : (hr.x - 8), width = 248 }, textTexture);
00446 
00447         }
