00453         public void OverlayInstructions(ref bool showInstructions, string instructions, string url, bool thin = false)
00454         {
00455             Rect r = thin ? EditorGUILayout.GetControlRect(false, 0) : EditorGUILayout.GetControlRect(true, 18);
00456             r.height = 18;
00457 
00458             if (instructions != null)
00459             {
00460 
00461                 EditorGUI.BeginDisabledGroup(true);
00462                 EditorGUI.LabelField(new Rect(r) { xMin = r.xMin + 14, yMin = r.yMin + 2 }, showInstrGC);
00463                 EditorGUI.EndDisabledGroup();
00464                 showInstructions = EditorGUI.Toggle(new Rect(r) { yMin = r.yMin + 2, width = EditorGUIUtility.labelWidth - 20 }, GUIContent.none, showInstructions, (GUIStyle)"Foldout");
00465 
00466                 DrawScriptField(serializedObject, new Rect(r) { yMin = r.yMin + 2 }, nolabel);
00467 
00468             }
00469             else
00470             {
00471                 DrawScriptField(serializedObject, new Rect(r) { yMin = r.yMin + 2 }, scirptlabel);
00472             }
00473 
00474 
00476             if (/*thin && */HelpURL != null)
00477             {
00478                 //var helpIcoRect = new Rect(r) { xMin = r.xMin - 8 - 16 - 2, width = 16 };
00479                 EditorUtils.DrawDocsIcon(r.xMin + EditorGUIUtility.labelWidth - 16 - 4, r.yMin + 2, url);
00480             }
00481         }
