00534         public static void DrawSerializedObjectFields(SerializedObject so, bool includeScriptField)
00535         {
00536             SerializedProperty sp = so.GetIterator();
00537             sp.Next(true);
00538 
00539             // Skip drawing the script reference?
00540             if (!includeScriptField)
00541                 sp.NextVisible(false);
00542 
00543             EditorGUI.BeginChangeCheck();
00544 
00545             int skipNextX = 0;
00546             int wrapNextX = 0;
00547 
00548             while (sp.NextVisible(false))
00549             {
00551                 if (skipNextX > 0)
00552                 {
00553                     skipNextX--;
00554                     continue;
00555                 }
00556 
00557                 EditorGUILayout.PropertyField(sp);
00558 
00559                 if (wrapNextX > 0)
00560                 {
00561                     wrapNextX--;
00562                     if (wrapNextX == 0)
00563                         EditorGUILayout.EndVertical();
00564                 }
00565 
00567                 var obj = sp.serializedObject.targetObject.GetType();
00568                 var fld = obj.GetField(sp.name);
00569                 if (fld != null)
00570                 {
00571                     var attrs = fld.GetCustomAttributes(false);
00572                     foreach (var a in attrs)
00573                     {
00574                         var hnx = a as HideNextXAttribute;
00575                         if (hnx != null)
00576                             if (sp.propertyType == SerializedPropertyType.Boolean)
00577                                 if (sp.boolValue == hnx.hideIf)
00578                                 {
00579                                     skipNextX = (a as HideNextXAttribute).hideCount;
00580                                 }
00581                                 else
00582                                 {
00583                                     wrapNextX = (a as HideNextXAttribute).hideCount;
00584                                     if (hnx.guiStyle != null || hnx.guiStyle == "")
00585                                         EditorGUILayout.BeginVertical((GUIStyle)hnx.guiStyle);
00586                                     else
00587                                         EditorGUILayout.BeginVertical();
00588                                 }
00589                     }
00590                 }
00591 
00592             }
00593 
00594             if (EditorGUI.EndChangeCheck())
00595             {
00596                 so.ApplyModifiedProperties();
00597             }
00598         }
