00660         protected static GUIContent GetGUIContent(SerializedProperty sp)
00661         {
00662             reusableGC.text = sp.displayName;
00663             reusableGC.tooltip = null;
00664 
00665             var type = sp.serializedObject.targetObject.GetType();
00666 
00667             var field = type.GetField(sp.name, (System.Reflection.BindingFlags)int.MaxValue);
00668 
00669             if (field == null)
00670             {
00671                 return reusableGC;
00672             }
00673 
00674             var attrs = field.GetCustomAttributes(true);
00675 
00676             foreach (var t in attrs)
00677             {
00678                 var tooltiptype = t as TooltipAttribute;
00679                 if (tooltiptype != null)
00680                 {
00681                     reusableGC.tooltip = tooltiptype.tooltip;
00682                 }
00683             }
00684 
00685             return reusableGC;
00686         }
