00473         public bool IsConnectedAndReady
00474         {
00475             get
00476             {
00477                 if (this.LoadBalancingPeer == null)
00478                 {
00479                     return false;
00480                 }
00481 
00482                 switch (this.State)
00483                 {
00484                     case ClientState.PeerCreated:
00485                     case ClientState.Disconnected:
00486                     case ClientState.Disconnecting:
00487                     case ClientState.DisconnectingFromGameServer:
00488                     case ClientState.DisconnectingFromMasterServer:
00489                     case ClientState.DisconnectingFromNameServer:
00490                     case ClientState.Authenticating:
00491                     case ClientState.ConnectingToGameServer:
00492                     case ClientState.ConnectingToMasterServer:
00493                     case ClientState.ConnectingToNameServer:
00494                     case ClientState.Joining:
00495                     case ClientState.Leaving:
00496                         return false;   // we are not ready to execute any operations
00497                 }
00498 
00499                 return true;
00500             }
00501         }
