00765         public LoadBalancingClient(ConnectionProtocol protocol = ConnectionProtocol.Udp)
00766         {
00767             this.ConnectionCallbackTargets = new ConnectionCallbacksContainer(this);
00768             this.MatchMakingCallbackTargets = new MatchMakingCallbacksContainer(this);
00769             this.InRoomCallbackTargets = new InRoomCallbacksContainer(this);
00770             this.LobbyCallbackTargets = new LobbyCallbacksContainer(this);
00771             this.WebRpcCallbackTargets = new WebRpcCallbacksContainer(this);
00772             this.ErrorInfoCallbackTargets = new ErrorInfoCallbacksContainer(this);
00773 
00774             this.LoadBalancingPeer = new LoadBalancingPeer(this, protocol);
00775             this.LoadBalancingPeer.OnDisconnectMessage += this.OnDisconnectMessageReceived;
00776             this.SerializationProtocol = SerializationProtocol.GpBinaryV18;
00777             this.LocalPlayer = this.CreatePlayer(string.Empty, -1, true, null); //TODO: Check if we can do this later
00778 
00779 
00780             #if SUPPORTED_UNITY
00781             CustomTypesUnity.Register();
00782             #endif
00783 
00784             #if UNITY_WEBGL
00785             if (this.LoadBalancingPeer.TransportProtocol == ConnectionProtocol.Tcp || this.LoadBalancingPeer.TransportProtocol == ConnectionProtocol.Udp)
00786             {
00787                 this.LoadBalancingPeer.Listener.DebugReturn(DebugLevel.WARNING, "WebGL requires WebSockets. Switching TransportProtocol to WebSocketSecure.");
00788                 this.LoadBalancingPeer.TransportProtocol = ConnectionProtocol.WebSocketSecure;
00789             }
00790             #endif
00791 
00792             this.State = ClientState.PeerCreated;
00793         }
