00814         private string GetNameServerAddress()
00815         {
00816             var protocolPort = 0;
00817             ProtocolToNameServerPort.TryGetValue(this.LoadBalancingPeer.TransportProtocol, out protocolPort);
00818 
00819             if (this.NameServerPortInAppSettings != 0)
00820             {
00821                 this.DebugReturn(DebugLevel.INFO, string.Format("Using NameServerPortInAppSettings: {0}", this.NameServerPortInAppSettings));
00822                 protocolPort = this.NameServerPortInAppSettings;
00823             }
00824 
00825             if (this.ServerPortOverrides.NameServerPort > 0)
00826             {
00827                 protocolPort = this.ServerPortOverrides.NameServerPort;
00828             }
00829 
00830             switch (this.LoadBalancingPeer.TransportProtocol)
00831             {
00832                 case ConnectionProtocol.Udp:
00833                 case ConnectionProtocol.Tcp:
00834                     return string.Format("{0}:{1}", NameServerHost, protocolPort);
00835                 case ConnectionProtocol.WebSocket:
00836                     return string.Format("ws://{0}:{1}", NameServerHost, protocolPort);
00837                 case ConnectionProtocol.WebSocketSecure:
00838                     return string.Format("wss://{0}:{1}", NameServerHost, protocolPort);
00839                 default:
00840                     throw new ArgumentOutOfRangeException();
00841             }
00842         }
