00848         // needed connect variants:
00849         // connect to Name Server only (could include getregions) -> end after getregions
00850         // connect to Region Master via Name Server (specific region/cluster) -> no getregions! authenticates and ends after on connected to master
00851         // connect to Best Region via Name Server
00852         // connect to Master Server (no Name Server, no appid)
00853 
00854         public virtual bool ConnectUsingSettings(AppSettings appSettings)
00855         {
00856             if (this.LoadBalancingPeer.PeerState != PeerStateValue.Disconnected)
00857             {
00858                 this.DebugReturn(DebugLevel.WARNING, "ConnectUsingSettings() failed. Can only connect while in state 'Disconnected'. Current state: " + this.LoadBalancingPeer.PeerState);
00859                 return false;
00860             }
00861 
00862             if (appSettings == null)
00863             {
00864                 this.DebugReturn(DebugLevel.ERROR, "ConnectUsingSettings failed. The appSettings can't be null.'");
00865                 return false;
00866             }
00867 
00868             switch (this.ClientType)
00869             {
00870                 case ClientAppType.Realtime:
00871                     this.AppId = appSettings.AppIdRealtime;
00872                     break;
00873                 case ClientAppType.Voice:
00874                     this.AppId = appSettings.AppIdVoice;
00875                     break;
00876                 case ClientAppType.Fusion:
00877                     this.AppId = appSettings.AppIdFusion;
00878                     break;
00879             }
00880 
00881             this.AppVersion = appSettings.AppVersion;
00882 
00883             this.IsUsingNameServer = appSettings.UseNameServer;
00884             this.CloudRegion = appSettings.FixedRegion;
00885             this.connectToBestRegion = string.IsNullOrEmpty(this.CloudRegion);
00886 
00887             this.EnableLobbyStatistics = appSettings.EnableLobbyStatistics;
00888             this.LoadBalancingPeer.DebugOut = appSettings.NetworkLogging;
00889 
00890             this.AuthMode = appSettings.AuthMode;
00891             if (appSettings.AuthMode == AuthModeOption.AuthOnceWss)
00892             {
00893                 this.LoadBalancingPeer.TransportProtocol = ConnectionProtocol.WebSocketSecure;
00894                 this.ExpectedProtocol = appSettings.Protocol;
00895             }
00896             else
00897             {
00898                 this.LoadBalancingPeer.TransportProtocol = appSettings.Protocol;
00899                 this.ExpectedProtocol = null;
00900             }
00901             
00902             this.EnableProtocolFallback = appSettings.EnableProtocolFallback;
00903 
00904             this.bestRegionSummaryFromStorage = appSettings.BestRegionSummaryFromStorage;
00905             this.DisconnectedCause = DisconnectCause.None;
00906 
00907 
00908             this.CheckConnectSetupWebGl();
00909 
00910 
00911             if (this.IsUsingNameServer)
00912             {
00913                 this.Server = ServerConnection.NameServer;
00914                 if (!appSettings.IsDefaultNameServer)
00915                 {
00916                     this.NameServerHost = appSettings.Server;
00917                 }
00918 
00919                 this.ProxyServerAddress = appSettings.ProxyServer;
00920                 this.NameServerPortInAppSettings = appSettings.Port;
00921                 if (!this.LoadBalancingPeer.Connect(this.NameServerAddress, this.ProxyServerAddress, this.AppId, this.TokenForInit))
00922                 {
00923                     return false;
00924                 }
00925 
00926                 this.State = ClientState.ConnectingToNameServer;
00927             }
00928             else
00929             {
00930                 this.Server = ServerConnection.MasterServer;
00931                 int portToUse = appSettings.IsDefaultPort ? 5055 : appSettings.Port;    // TODO: setup new (default) port config
00932                 this.MasterServerAddress = string.Format("{0}:{1}", appSettings.Server, portToUse);
00933 
00934                 if (!this.LoadBalancingPeer.Connect(this.MasterServerAddress, this.ProxyServerAddress, this.AppId, this.TokenForInit))
00935                 {
00936                     return false;
00937                 }
00938 
00939                 this.State = ClientState.ConnectingToMasterServer;
00940             }
00941 
00942             return true;
00943         }
