00946         [Obsolete("Use ConnectToMasterServer() instead.")]
00947         public bool Connect()
00948         {
00949             return this.ConnectToMasterServer();
00950         }
00951 
00971         public virtual bool ConnectToMasterServer()
00972         {
00973             if (this.LoadBalancingPeer.PeerState != PeerStateValue.Disconnected)
00974             {
00975                 this.DebugReturn(DebugLevel.WARNING, "ConnectToMasterServer() failed. Can only connect while in state 'Disconnected'. Current state: " + this.LoadBalancingPeer.PeerState);
00976                 return false;
00977             }
00978 
00979             // when using authMode AuthOnce or AuthOnceWSS, the token must be available for the init request. if it's null in that case, don't connect
00980             if (this.AuthMode != AuthModeOption.Auth && this.TokenForInit == null)
00981             {
00982                 this.DebugReturn(DebugLevel.ERROR, "Connect() failed. Can't connect to MasterServer with Token == null in AuthMode: " + this.AuthMode);
00983                 return false;
00984             }
00985 
00986             this.CheckConnectSetupWebGl();
00987 
00988             if (this.LoadBalancingPeer.Connect(this.MasterServerAddress, this.ProxyServerAddress, this.AppId, this.TokenForInit))
00989             {
00990                 this.DisconnectedCause = DisconnectCause.None;
00991                 this.connectToBestRegion = false;
00992                 this.State = ClientState.ConnectingToMasterServer;
00993                 this.Server = ServerConnection.MasterServer;
00994                 return true;
00995             }
00996 
00997             return false;
00998         }
00999 
01000 
01006         public bool ConnectToNameServer()
01007         {
01008             if (this.LoadBalancingPeer.PeerState != PeerStateValue.Disconnected)
01009             {
01010                 this.DebugReturn(DebugLevel.WARNING, "ConnectToNameServer() failed. Can only connect while in state 'Disconnected'. Current state: " + this.LoadBalancingPeer.PeerState);
01011                 return false;
01012             }
01013 
01014             this.IsUsingNameServer = true;
01015             this.CloudRegion = null;
01016 
01017 
01018             this.CheckConnectSetupWebGl();
01019 
01020 
01021             if (this.AuthMode == AuthModeOption.AuthOnceWss)
01022             {
01023                 if (this.ExpectedProtocol == null)
01024                 {
01025                     this.ExpectedProtocol = this.LoadBalancingPeer.TransportProtocol;
01026                 }
01027                 this.LoadBalancingPeer.TransportProtocol = ConnectionProtocol.WebSocketSecure;
01028             }
01029 
01030             if (this.LoadBalancingPeer.Connect(this.NameServerAddress, this.ProxyServerAddress, "NameServer", this.TokenForInit))
01031             {
01032                 this.DisconnectedCause = DisconnectCause.None;
01033                 this.connectToBestRegion = false;
01034                 this.State = ClientState.ConnectingToNameServer;
01035                 this.Server = ServerConnection.NameServer;
01036                 return true;
01037             }
01038 
01039             return false;
01040         }
01041 
01054         public bool ConnectToRegionMaster(string region)
01055         {
01056             if (string.IsNullOrEmpty(region))
01057             {
01058                 this.DebugReturn(DebugLevel.ERROR, "ConnectToRegionMaster() failed. The region can not be null or empty.");
01059                 return false;
01060             }
01061 
01062             this.IsUsingNameServer = true;
01063 
01064             if (this.State == ClientState.Authenticating)
01065             {
01066                 if (this.LoadBalancingPeer.DebugOut >= DebugLevel.INFO)
01067                 {
01068                     this.DebugReturn(DebugLevel.INFO, "ConnectToRegionMaster() will skip calling authenticate, as the current state is 'Authenticating'. Just wait for the result.");
01069                 }
01070                 return true;
01071             }
01072 
01073             if (this.State == ClientState.ConnectedToNameServer)
01074             {
01075                 this.CloudRegion = region;
01076 
01077                 bool authenticating = this.CallAuthenticate();
01078                 if (authenticating)
01079                 {
01080                     this.State = ClientState.Authenticating;
01081                 }
01082 
01083                 return authenticating;
01084             }
01085 
01086 
01087             this.LoadBalancingPeer.Disconnect();
01088 
01089             if (!string.IsNullOrEmpty(region) && !region.Contains("/"))
01090             {
01091                 region = region + "/*";
01092             }
01093             this.CloudRegion = region;
01094 
01095 
01096             this.CheckConnectSetupWebGl();
01097 
01098 
01099             if (this.AuthMode == AuthModeOption.AuthOnceWss)
01100             {
01101                 if (this.ExpectedProtocol == null)
01102                 {
01103                     this.ExpectedProtocol = this.LoadBalancingPeer.TransportProtocol;
01104                 }
01105                 this.LoadBalancingPeer.TransportProtocol = ConnectionProtocol.WebSocketSecure;
01106             }
01107 
01108             this.connectToBestRegion = false;
01109             this.DisconnectedCause = DisconnectCause.None;
01110             if (!this.LoadBalancingPeer.Connect(this.NameServerAddress, this.ProxyServerAddress, "NameServer", null))
01111             {
01112                 return false;
01113             }
01114 
01115             this.State = ClientState.ConnectingToNameServer;
01116             this.Server = ServerConnection.NameServer;
01117             return true;
01118         }
01119 
01120         [Conditional("UNITY_WEBGL")]
01121         private void CheckConnectSetupWebGl()
01122         {
01123             #if UNITY_WEBGL
01124             if (this.LoadBalancingPeer.TransportProtocol != ConnectionProtocol.WebSocket && this.LoadBalancingPeer.TransportProtocol != ConnectionProtocol.WebSocketSecure)
01125             {
01126                 this.DebugReturn(DebugLevel.WARNING, "WebGL requires WebSockets. Switching TransportProtocol to WebSocketSecure.");
01127                 this.LoadBalancingPeer.TransportProtocol = ConnectionProtocol.WebSocketSecure;
01128             }
01129 
01130             this.EnableProtocolFallback = false; // no fallback on WebGL
01131             #endif
01132         }
01133 
01137         private bool Connect(string serverAddress, string proxyServerAddress, ServerConnection serverType)
01138         {
01139             // TODO: Make sure app doesn't quit right now
01140 
01141             if (this.State == ClientState.Disconnecting)
01142             {
01143                 this.DebugReturn(DebugLevel.ERROR, "Connect() failed. Can't connect while disconnecting (still). Current state: " + this.State);
01144                 return false;
01145             }
01146 
01147             // when using authMode AuthOnce or AuthOnceWSS, the token must be available for the init request. if it's null in that case, don't connect
01148             if (this.AuthMode != AuthModeOption.Auth && serverType != ServerConnection.NameServer && this.TokenForInit == null)
01149             {
01150                 this.DebugReturn(DebugLevel.ERROR, "Connect() failed. Can't connect to " + serverType + " with Token == null in AuthMode: " + this.AuthMode);
01151                 return false;
01152             }
01153 
01154             // connect might fail, if the DNS name can't be resolved or if no network connection is available, etc.
01155             bool connecting = this.LoadBalancingPeer.Connect(serverAddress, proxyServerAddress, this.AppId, this.TokenForInit);
01156 
01157             if (connecting)
01158             {
01159                 this.DisconnectedCause = DisconnectCause.None;
01160                 this.Server = serverType;
01161 
01162                 switch (serverType)
01163                 {
01164                     case ServerConnection.NameServer:
01165                         State = ClientState.ConnectingToNameServer;
01166                         break;
01167                     case ServerConnection.MasterServer:
01168                         State = ClientState.ConnectingToMasterServer;
01169                         break;
01170                     case ServerConnection.GameServer:
01171                         State = ClientState.ConnectingToGameServer;
01172                         break;
01173                 }
01174             }
01175 
01176             return connecting;
01177         }
01178 
01179 
01182         public bool ReconnectToMaster()
01183         {
01184             if (this.LoadBalancingPeer.PeerState != PeerStateValue.Disconnected)
01185             {
01186                 this.DebugReturn(DebugLevel.WARNING, "ReconnectToMaster() failed. Can only connect while in state 'Disconnected'. Current state: " + this.LoadBalancingPeer.PeerState);
01187                 return false;
01188             }
01189 
01190             if (string.IsNullOrEmpty(this.MasterServerAddress))
01191             {
01192                 this.DebugReturn(DebugLevel.WARNING, "ReconnectToMaster() failed. MasterServerAddress is null or empty.");
01193                 return false;
01194             }
01195             if (this.tokenCache == null)
01196             {
01197                 this.DebugReturn(DebugLevel.WARNING, "ReconnectToMaster() failed. It seems the client doesn't have any previous authentication token to re-connect.");
01198                 return false;
01199             }
01200 
01201             if (this.AuthValues == null)
01202             {
01203                 this.DebugReturn(DebugLevel.WARNING, "ReconnectToMaster() with AuthValues == null is not correct!");
01204                 this.AuthValues = new AuthenticationValues();
01205             }
01206             this.AuthValues.Token = this.tokenCache;
01207 
01208             return this.Connect(this.MasterServerAddress, this.ProxyServerAddress, ServerConnection.MasterServer);
01209         }
01210 
01219         public bool ReconnectAndRejoin()
01220         {
01221             if (this.LoadBalancingPeer.PeerState != PeerStateValue.Disconnected)
01222             {
01223                 this.DebugReturn(DebugLevel.WARNING, "ReconnectAndRejoin() failed. Can only connect while in state 'Disconnected'. Current state: " + this.LoadBalancingPeer.PeerState);
01224                 return false;
01225             }
01226 
01227             if (string.IsNullOrEmpty(this.GameServerAddress))
01228             {
01229                 this.DebugReturn(DebugLevel.WARNING, "ReconnectAndRejoin() failed. It seems the client wasn't connected to a game server before (no address).");
01230                 return false;
01231             }
01232             if (this.enterRoomParamsCache == null)
01233             {
01234                 this.DebugReturn(DebugLevel.WARNING, "ReconnectAndRejoin() failed. It seems the client doesn't have any previous room to re-join.");
01235                 return false;
01236             }
01237             if (this.tokenCache == null)
01238             {
01239                 this.DebugReturn(DebugLevel.WARNING, "ReconnectAndRejoin() failed. It seems the client doesn't have any previous authentication token to re-connect.");
01240                 return false;
01241             }
01242 
01243             if (this.AuthValues == null)
01244             {
01245                 this.AuthValues = new AuthenticationValues();
01246             }
01247             this.AuthValues.Token = this.tokenCache;
01248 
01249 
01250             if (!string.IsNullOrEmpty(this.GameServerAddress) && this.enterRoomParamsCache != null)
01251             {
01252                 this.lastJoinType = JoinType.JoinRoom;
01253                 this.enterRoomParamsCache.JoinMode = JoinMode.RejoinOnly;
01254                 return this.Connect(this.GameServerAddress, this.ProxyServerAddress, ServerConnection.GameServer);
01255             }
01256 
01257             return false;
01258         }
01259 
01260 
01270         public void Disconnect(DisconnectCause cause = DisconnectCause.DisconnectByClientLogic)
01271         {
01272             if (this.State == ClientState.Disconnecting || this.State == ClientState.PeerCreated)
01273             {
01274                 this.DebugReturn(DebugLevel.INFO, "Disconnect() call gets skipped due to State " + this.State + ". DisconnectedCause: " + this.DisconnectedCause + " Parameter cause: " + cause);
01275                 return;
01276             }
01277 
01278             if (this.State != ClientState.Disconnected)
01279             {
01280                 this.State = ClientState.Disconnecting;
01281                 this.DisconnectedCause = cause;
01282                 this.LoadBalancingPeer.Disconnect();
01283             }
01284         }
