01290         private void DisconnectToReconnect()
01291         {
01292             switch (this.Server)
01293             {
01294                 case ServerConnection.NameServer:
01295                     this.State = ClientState.DisconnectingFromNameServer;
01296                     break;
01297                 case ServerConnection.MasterServer:
01298                     this.State = ClientState.DisconnectingFromMasterServer;
01299                     break;
01300                 case ServerConnection.GameServer:
01301                     this.State = ClientState.DisconnectingFromGameServer;
01302                     break;
01303             }
01304 
01305             this.LoadBalancingPeer.Disconnect();
01306         }
