01327         public void SimulateConnectionLoss(bool simulateTimeout)
01328         {
01329             this.DebugReturn(DebugLevel.WARNING, "SimulateConnectionLoss() set to: "+simulateTimeout);
01330 
01331             if (simulateTimeout)
01332             {
01333                 this.LoadBalancingPeer.NetworkSimulationSettings.IncomingLossPercentage = 100;
01334                 this.LoadBalancingPeer.NetworkSimulationSettings.OutgoingLossPercentage = 100;
01335             }
01336 
01337             this.LoadBalancingPeer.IsSimulationEnabled = simulateTimeout;
01338         }
