01444         public bool OpFindFriends(string[] friendsToFind, FindFriendsOptions options = null)
01445         {
01446             if (!this.CheckIfOpCanBeSent(OperationCode.FindFriends, this.Server, "FindFriends"))
01447             {
01448                 return false;
01449             }
01450 
01451             if (this.IsFetchingFriendList)
01452             {
01453                 this.DebugReturn(DebugLevel.WARNING, "OpFindFriends skipped: already fetching friends list.");
01454                 return false;   // fetching friends currently, so don't do it again (avoid changing the list while fetching friends)
01455             }
01456 
01457             if (friendsToFind == null || friendsToFind.Length == 0)
01458             {
01459                 this.DebugReturn(DebugLevel.ERROR, "OpFindFriends skipped: friendsToFind array is null or empty.");
01460                 return false;
01461             }
01462 
01463             if (friendsToFind.Length > FriendRequestListMax)
01464             {
01465                 this.DebugReturn(DebugLevel.ERROR, string.Format("OpFindFriends skipped: friendsToFind array exceeds allowed length of {0}.", FriendRequestListMax));
01466                 return false;
01467             }
01468 
01469             List<string> friendsList = new List<string>(friendsToFind.Length);
01470             for (int i = 0; i < friendsToFind.Length; i++)
01471             {
01472                 string friendUserId = friendsToFind[i];
01473                 if (string.IsNullOrEmpty(friendUserId))
01474                 {
01475                     this.DebugReturn(DebugLevel.WARNING,
01476                         string.Format(
01477                             "friendsToFind array contains a null or empty UserId, element at position {0} skipped.",
01478                             i));
01479                 }
01480                 else if (friendUserId.Equals(UserId))
01481                 {
01482                     this.DebugReturn(DebugLevel.WARNING,
01483                         string.Format(
01484                             "friendsToFind array contains local player's UserId \"{0}\", element at position {1} skipped.",
01485                             friendUserId,
01486                             i));
01487                 }
01488                 else if (friendsList.Contains(friendUserId))
01489                 {
01490                     this.DebugReturn(DebugLevel.WARNING,
01491                         string.Format(
01492                             "friendsToFind array contains duplicate UserId \"{0}\", element at position {1} skipped.",
01493                             friendUserId,
01494                             i));
01495                 }
01496                 else
01497                 {
01498                     friendsList.Add(friendUserId);
01499                 }
01500             }
01501 
01502             if (friendsList.Count == 0)
01503             {
01504                 this.DebugReturn(DebugLevel.ERROR, "OpFindFriends skipped: friends list to find is empty.");
01505                 return false;
01506             }
01507 
01508             string[] filteredArray = friendsList.ToArray();
01509             bool sent = this.LoadBalancingPeer.OpFindFriends(filteredArray, options);
01510             this.friendListRequested = sent ? filteredArray : null;
01511 
01512             return sent;
01513         }
