02005         public bool OpSetCustomPropertiesOfActor(int actorNr, Hashtable propertiesToSet, Hashtable expectedProperties = null, WebFlags webFlags = null)
02006         {
02007             if (propertiesToSet == null || propertiesToSet.Count == 0)
02008             {
02009                 this.DebugReturn(DebugLevel.ERROR, "OpSetCustomPropertiesOfActor() failed. propertiesToSet must not be null nor empty.");
02010                 return false;
02011             }
02012 
02013             if (this.CurrentRoom == null)
02014             {
02015                 // if you attempt to set this player's values without conditions, then fine:
02016                 if (expectedProperties == null && webFlags == null && this.LocalPlayer != null && this.LocalPlayer.ActorNumber == actorNr)
02017                 {
02018                     return this.LocalPlayer.SetCustomProperties(propertiesToSet);
02019                 }
02020 
02021                 if (this.LoadBalancingPeer.DebugOut >= DebugLevel.ERROR)
02022                 {
02023                     this.DebugReturn(DebugLevel.ERROR, "OpSetCustomPropertiesOfActor() failed. To use expectedProperties or webForward, you have to be in a room. State: " + this.State);
02024                 }
02025                 return false;
02026             }
02027 
02028             Hashtable customActorProperties = new Hashtable();
02029             customActorProperties.MergeStringKeys(propertiesToSet);
02030             if (customActorProperties.Count == 0)
02031             {
02032                 this.DebugReturn(DebugLevel.ERROR, "OpSetCustomPropertiesOfActor() failed. Only string keys allowed for custom properties.");
02033                 return false;
02034             }
02035             return this.OpSetPropertiesOfActor(actorNr, customActorProperties, expectedProperties, webFlags);
02036         }
