02041         protected internal bool OpSetPropertiesOfActor(int actorNr, Hashtable actorProperties, Hashtable expectedProperties = null, WebFlags webFlags = null)
02042         {
02043             if (!this.CheckIfOpCanBeSent(OperationCode.SetProperties, this.Server, "SetProperties"))
02044             {
02045                 return false;
02046             }
02047 
02048             if (actorProperties == null || actorProperties.Count == 0)
02049             {
02050                 this.DebugReturn(DebugLevel.ERROR, "OpSetPropertiesOfActor() failed. actorProperties must not be null nor empty.");
02051                 return false;
02052             }
02053             bool res = this.LoadBalancingPeer.OpSetPropertiesOfActor(actorNr, actorProperties, expectedProperties, webFlags);
02054             if (res && !this.CurrentRoom.BroadcastPropertiesChangeToAll && (expectedProperties == null || expectedProperties.Count == 0))
02055             {
02056                 Player target = this.CurrentRoom.GetPlayer(actorNr);
02057                 if (target != null)
02058                 {
02059                     target.InternalCacheProperties(actorProperties);
02060                     this.InRoomCallbackTargets.OnPlayerPropertiesUpdate(target, actorProperties);
02061                 }
02062             }
02063             return res;
02064         }
