02213         private void ReadoutProperties(Hashtable gameProperties, Hashtable actorProperties, int targetActorNr)
02214         {
02215             // read game properties and cache them locally
02216             if (this.CurrentRoom != null && gameProperties != null)
02217             {
02218                 this.CurrentRoom.InternalCacheProperties(gameProperties);
02219                 if (this.InRoom)
02220                 {
02221                     this.InRoomCallbackTargets.OnRoomPropertiesUpdate(gameProperties);
02222                 }
02223             }
02224 
02225             if (actorProperties != null && actorProperties.Count > 0)
02226             {
02227                 if (targetActorNr > 0)
02228                 {
02229                     // we have a single entry in the actorProperties with one user's name
02230                     // targets MUST exist before you set properties
02231                     Player target = this.CurrentRoom.GetPlayer(targetActorNr);
02232                     if (target != null)
02233                     {
02234                         Hashtable props = this.ReadoutPropertiesForActorNr(actorProperties, targetActorNr);
02235                         target.InternalCacheProperties(props);
02236                         this.InRoomCallbackTargets.OnPlayerPropertiesUpdate(target, props);
02237                     }
02238                 }
02239                 else
02240                 {
02241                     // in this case, we've got a key-value pair per actor (each
02242                     // value is a hashtable with the actor's properties then)
02243                     int actorNr;
02244                     Hashtable props;
02245                     string newName;
02246                     Player target;
02247 
02248                     foreach (object key in actorProperties.Keys)
02249                     {
02250                         actorNr = (int)key;
02251                         if (actorNr == 0)
02252                         {
02253                             continue;
02254                         }
02255 
02256                         props = (Hashtable)actorProperties[key];
02257                         newName = (string)props[ActorProperties.PlayerName];
02258 
02259                         target = this.CurrentRoom.GetPlayer(actorNr);
02260                         if (target == null)
02261                         {
02262                             target = this.CreatePlayer(newName, actorNr, false, props);
02263                             this.CurrentRoom.StorePlayer(target);
02264                         }
02265                         target.InternalCacheProperties(props);
02266                     }
02267                 }
02268             }
02269         }
02270 
02271 
02275         private Hashtable ReadoutPropertiesForActorNr(Hashtable actorProperties, int actorNr)
02276         {
02277             if (actorProperties.ContainsKey(actorNr))
02278             {
02279                 return (Hashtable)actorProperties[actorNr];
02280             }
02281 
02282             return actorProperties;
02283         }
02284 
02289         public void ChangeLocalID(int newID)
02290         {
02291             if (this.LocalPlayer == null)
02292             {
02293                 this.DebugReturn(DebugLevel.WARNING, string.Format("Local actor is null or not in mActors! mLocalActor: {0} mActors==null: {1} newID: {2}", this.LocalPlayer, this.CurrentRoom.Players == null, newID));
02294             }
02295 
02296             if (this.CurrentRoom == null)
02297             {
02298                 // change to new actor/player ID and make sure the player does not have a room reference left
02299                 this.LocalPlayer.ChangeLocalID(newID);
02300                 this.LocalPlayer.RoomReference = null;
02301             }
02302             else
02303             {
02304                 // remove old actorId from actor list
02305                 this.CurrentRoom.RemovePlayer(this.LocalPlayer);
02306 
02307                 // change to new actor/player ID
02308                 this.LocalPlayer.ChangeLocalID(newID);
02309 
02310                 // update the room's list with the new reference
02311                 this.CurrentRoom.StorePlayer(this.LocalPlayer);
02312             }
02313         }
02314 
02315 
02316 
02325         private void GameEnteredOnGameServer(OperationResponse operationResponse)
02326         {
02327             this.CurrentRoom = this.CreateRoom(this.enterRoomParamsCache.RoomName, this.enterRoomParamsCache.RoomOptions);
02328             this.CurrentRoom.LoadBalancingClient = this;
02329 
02330             // first change the local id, instead of first updating the actorList since actorList uses ID to update itself
02331 
02332             // the local player's actor-properties are not returned in join-result. add this player to the list
02333             int localActorNr = (int)operationResponse[ParameterCode.ActorNr];
02334             this.ChangeLocalID(localActorNr);
02335 
02336             if (operationResponse.Parameters.ContainsKey(ParameterCode.ActorList))
02337             {
02338                 int[] actorsInRoom = (int[])operationResponse.Parameters[ParameterCode.ActorList];
02339                 this.UpdatedActorList(actorsInRoom);
02340             }
02341 
02342 
02343             Hashtable actorProperties = (Hashtable)operationResponse[ParameterCode.PlayerProperties];
02344             Hashtable gameProperties = (Hashtable)operationResponse[ParameterCode.GameProperties];
02345             this.ReadoutProperties(gameProperties, actorProperties, 0);
02346 
02347             object temp;
02348             if (operationResponse.Parameters.TryGetValue(ParameterCode.RoomOptionFlags, out temp))
02349             {
02350                 this.CurrentRoom.InternalCacheRoomFlags((int)temp);
02351             }
02352 
02353             this.State = ClientState.Joined;
02354 
02355 
02356             // the callbacks OnCreatedRoom and OnJoinedRoom are called in the event join. it contains important info about the room and players.
02357             // unless there will be no room events (RoomOptions.SuppressRoomEvents = true)
02358             if (this.CurrentRoom.SuppressRoomEvents)
02359             {
02360                 if (this.lastJoinType == JoinType.CreateRoom || (this.lastJoinType == JoinType.JoinOrCreateRoom && this.LocalPlayer.ActorNumber == 1))
02361                 {
02362                     this.MatchMakingCallbackTargets.OnCreatedRoom();
02363                 }
02364 
02365                 this.MatchMakingCallbackTargets.OnJoinedRoom();
02366             }
02367         }
02368 
02369 
02370         private void UpdatedActorList(int[] actorsInGame)
02371         {
02372             if (actorsInGame != null)
02373             {
02374                 foreach (int actorNumber in actorsInGame)
02375                 {
02376                     if (actorNumber == 0)
02377                     {
02378                         continue;
02379                     }
02380 
02381                     Player target = this.CurrentRoom.GetPlayer(actorNumber);
02382                     if (target == null)
02383                     {
02384                         this.CurrentRoom.StorePlayer(this.CreatePlayer(string.Empty, actorNumber, false, null));
02385                     }
02386                 }
02387             }
02388         }
02389 
02398         protected internal virtual Player CreatePlayer(string actorName, int actorNumber, bool isLocal, Hashtable actorProperties)
02399         {
02400             Player newPlayer = new Player(actorName, actorNumber, isLocal, actorProperties);
02401             return newPlayer;
02402         }
02403 
02405         protected internal virtual Room CreateRoom(string roomName, RoomOptions opt)
02406         {
02407             Room r = new Room(roomName, opt);
02408             return r;
02409         }
02410 
02411         private bool CheckIfOpAllowedOnServer(byte opCode, ServerConnection serverConnection)
02412         {
02413             switch (serverConnection)
02414             {
02415                 case ServerConnection.MasterServer:
02416                     switch (opCode)
02417                     {
02418                         case OperationCode.CreateGame:
02419                         case OperationCode.Authenticate:
02420                         case OperationCode.AuthenticateOnce:
02421                         case OperationCode.FindFriends:
02422                         case OperationCode.GetGameList:
02423                         case OperationCode.GetLobbyStats:
02424                         case OperationCode.JoinGame:
02425                         case OperationCode.JoinLobby:
02426                         case OperationCode.LeaveLobby:
02427                         case OperationCode.WebRpc:
02428                         case OperationCode.ServerSettings:
02429                         case OperationCode.JoinRandomGame:
02430                             return true;
02431                     }
02432                     break;
02433                 case ServerConnection.GameServer:
02434                     switch (opCode)
02435                     {
02436                         case OperationCode.CreateGame:
02437                         case OperationCode.Authenticate:
02438                         case OperationCode.AuthenticateOnce:
02439                         case OperationCode.ChangeGroups:
02440                         case OperationCode.GetProperties:
02441                         case OperationCode.JoinGame:
02442                         case OperationCode.Leave:
02443                         case OperationCode.WebRpc:
02444                         case OperationCode.ServerSettings:
02445                         case OperationCode.SetProperties:
02446                         case OperationCode.RaiseEvent:
02447                             return true;
02448                     }
02449                     break;
02450                 case ServerConnection.NameServer:
02451                     switch (opCode)
02452                     {
02453                         case OperationCode.Authenticate:
02454                         case OperationCode.AuthenticateOnce:
02455                         case OperationCode.GetRegions:
02456                         case OperationCode.ServerSettings:
02457                             return true;
02458                     }
02459                     break;
02460                 default:
02461                     throw new ArgumentOutOfRangeException("serverConnection", serverConnection, null);
02462             }
02463             return false;
02464         }
02465 
02466         private bool CheckIfOpCanBeSent(byte opCode, ServerConnection serverConnection, string opName)
02467         {
02468             if (this.LoadBalancingPeer == null)
02469             {
02470                 this.DebugReturn(DebugLevel.ERROR, string.Format("Operation {0} ({1}) can't be sent because peer is null", opName, opCode));
02471                 return false;
02472             }
02473 
02474             if (!this.CheckIfOpAllowedOnServer(opCode, serverConnection))
02475             {
02476                 if (this.LoadBalancingPeer.DebugOut >= DebugLevel.ERROR)
02477                 {
02478                     this.DebugReturn(DebugLevel.ERROR, string.Format("Operation {0} ({1}) not allowed on current server ({2})", opName, opCode, serverConnection));
02479                 }
02480                 return false;
02481             }
02482 
02483             if (!this.CheckIfClientIsReadyToCallOperation(opCode))
02484             {
02485                 DebugLevel levelToReport = DebugLevel.ERROR;
02486                 if (opCode == OperationCode.RaiseEvent && (this.State == ClientState.Leaving || this.State == ClientState.Disconnecting || this.State == ClientState.DisconnectingFromGameServer))
02487                 {
02488                     levelToReport = DebugLevel.INFO;
02489                 }
02490 
02491                 if (this.LoadBalancingPeer.DebugOut >= levelToReport)
02492                 {
02493                     this.DebugReturn(levelToReport, string.Format("Operation {0} ({1}) not called because client is not connected or not ready yet, client state: {2}", opName, opCode, Enum.GetName(typeof(ClientState), this.State)));
02494                 }
02495 
02496                 return false;
02497             }
02498 
02499             if (this.LoadBalancingPeer.PeerState != PeerStateValue.Connected)
02500             {
02501                 this.DebugReturn(DebugLevel.ERROR, string.Format("Operation {0} ({1}) can't be sent because peer is not connected, peer state: {2}", opName, opCode, this.LoadBalancingPeer.PeerState));
02502                 return false;
02503             }
02504             return true;
02505         }
02506 
02507         private bool CheckIfClientIsReadyToCallOperation(byte opCode)
02508         {
02509             switch (opCode)
02510             {
02511                 //case OperationCode.ServerSettings: // ??
02512                 //case OperationCode.WebRpc: // WebRPC works on MS and GS and I think it does not need the client to be ready
02513 
02514                 case OperationCode.Authenticate:
02515                 case OperationCode.AuthenticateOnce:
02516                     return this.IsConnectedAndReady ||
02517                          this.State == ClientState.ConnectingToNameServer || // this is required since we do not set state to ConnectedToNameServer before authentication
02518                         this.State == ClientState.ConnectingToMasterServer || // this is required since we do not set state to ConnectedToMasterServer before authentication
02519                         this.State == ClientState.ConnectingToGameServer; // this is required since we do not set state to ConnectedToGameServer before authentication
02520 
02521                 case OperationCode.ChangeGroups:
02522                 case OperationCode.GetProperties:
02523                 case OperationCode.SetProperties:
02524                 case OperationCode.RaiseEvent:
02525                 case OperationCode.Leave:
02526                     return this.InRoom;
02527 
02528                 case OperationCode.JoinGame:
02529                 case OperationCode.CreateGame:
02530                     return this.State == ClientState.ConnectedToMasterServer || this.InLobby || this.State == ClientState.ConnectedToGameServer; // CurrentRoom can be not null in case of quick rejoin
02531 
02532                 case OperationCode.LeaveLobby:
02533                     return this.InLobby;
02534 
02535                 case OperationCode.JoinRandomGame:
02536                 case OperationCode.FindFriends:
02537                 case OperationCode.GetGameList:
02538                 case OperationCode.GetLobbyStats: // do we need to be inside lobby to call this?
02539                 case OperationCode.JoinLobby: // You don't have to explicitly leave a lobby to join another (client can be in one max, at any time)
02540                     return this.State == ClientState.ConnectedToMasterServer || this.InLobby;
02541                 case OperationCode.GetRegions:
02542                     return this.State == ClientState.ConnectedToNameServer;
02543             }
02544             return this.IsConnected;
02545         }
02546 
02547         #endregion
02548 
02549         #region Implementation of IPhotonPeerListener
02550 
02553         public virtual void DebugReturn(DebugLevel level, string message)
02554         {
02555             if (this.LoadBalancingPeer.DebugOut != DebugLevel.ALL && level > this.LoadBalancingPeer.DebugOut)
02556             {
02557                 return;
02558             }
02559             #if !SUPPORTED_UNITY
02560             Debug.WriteLine(message);
02561             #else
02562             if (level == DebugLevel.ERROR)
02563             {
02564                 Debug.LogError(message);
02565             }
02566             else if (level == DebugLevel.WARNING)
02567             {
02568                 Debug.LogWarning(message);
02569             }
02570             else if (level == DebugLevel.INFO)
02571             {
02572                 Debug.Log(message);
02573             }
02574             else if (level == DebugLevel.ALL)
02575             {
02576                 Debug.Log(message);
02577             }
02578             #endif
02579         }
02580 
02581         private void CallbackRoomEnterFailed(OperationResponse operationResponse)
02582         {
02583             if (operationResponse.ReturnCode != 0)
02584             {
02585                 if (operationResponse.OperationCode == OperationCode.JoinGame)
02586                 {
02587                     this.MatchMakingCallbackTargets.OnJoinRoomFailed(operationResponse.ReturnCode, operationResponse.DebugMessage);
02588                 }
02589                 else if (operationResponse.OperationCode == OperationCode.CreateGame)
02590                 {
02591                     this.MatchMakingCallbackTargets.OnCreateRoomFailed(operationResponse.ReturnCode, operationResponse.DebugMessage);
02592                 }
02593                 else if (operationResponse.OperationCode == OperationCode.JoinRandomGame)
02594                 {
02595                     this.MatchMakingCallbackTargets.OnJoinRandomFailed(operationResponse.ReturnCode, operationResponse.DebugMessage);
02596                 }
02597             }
02598         }
02599 
02614         public virtual void OnOperationResponse(OperationResponse operationResponse)
02615         {
02616             // if (operationResponse.ReturnCode != 0) this.DebugReturn(DebugLevel.ERROR, operationResponse.ToStringFull());
02617 
02618             // use the "secret" or "token" whenever we get it. doesn't really matter if it's in AuthResponse.
02619             if (operationResponse.Parameters.ContainsKey(ParameterCode.Token))
02620             {
02621                 if (this.AuthValues == null)
02622                 {
02623                     this.AuthValues = new AuthenticationValues();
02624                     //this.DebugReturn(DebugLevel.ERROR, "Server returned secret. Created AuthValues.");
02625                 }
02626 
02627                 this.AuthValues.Token = operationResponse[ParameterCode.Token] as string;
02628                 this.tokenCache = this.AuthValues.Token;
02629             }
02630 
02631             // if the operation limit was reached, disconnect (but still execute the operation response).
02632             if (operationResponse.ReturnCode == ErrorCode.OperationLimitReached)
02633             {
02634                 this.Disconnect(DisconnectCause.DisconnectByOperationLimit);
02635             }
02636 
02637             switch (operationResponse.OperationCode)
02638             {
02639                 case OperationCode.Authenticate:
02640                 case OperationCode.AuthenticateOnce:
02641                     {
02642                         if (operationResponse.ReturnCode != 0)
02643                         {
02644                             this.DebugReturn(DebugLevel.ERROR, operationResponse.ToStringFull() + " Server: " + this.Server + " Address: " + this.LoadBalancingPeer.ServerAddress);
02645 
02646                             switch (operationResponse.ReturnCode)
02647                             {
02648                                 case ErrorCode.InvalidAuthentication:
02649                                     this.DisconnectedCause = DisconnectCause.InvalidAuthentication;
02650                                     break;
02651                                 case ErrorCode.CustomAuthenticationFailed:
02652                                     this.DisconnectedCause = DisconnectCause.CustomAuthenticationFailed;
02653                                     this.ConnectionCallbackTargets.OnCustomAuthenticationFailed(operationResponse.DebugMessage);
02654                                     break;
02655                                 case ErrorCode.InvalidRegion:
02656                                     this.DisconnectedCause = DisconnectCause.InvalidRegion;
02657                                     break;
02658                                 case ErrorCode.MaxCcuReached:
02659                                     this.DisconnectedCause = DisconnectCause.MaxCcuReached;
02660                                     break;
02661                                 case ErrorCode.InvalidOperation:
02662                                 case ErrorCode.OperationNotAllowedInCurrentState:
02663                                     this.DisconnectedCause = DisconnectCause.OperationNotAllowedInCurrentState;
02664                                     break;
02665                                 case ErrorCode.AuthenticationTicketExpired:
02666                                     this.DisconnectedCause = DisconnectCause.AuthenticationTicketExpired;
02667                                     break;
02668                             }
02669 
02670                             this.Disconnect(this.DisconnectedCause);
02671                             break;  // if auth didn't succeed, we disconnect (above) and exit this operation's handling
02672                         }
02673 
02674                         if (this.Server == ServerConnection.NameServer || this.Server == ServerConnection.MasterServer)
02675                         {
02676                             if (operationResponse.Parameters.ContainsKey(ParameterCode.UserId))
02677                             {
02678                                 string incomingId = (string)operationResponse.Parameters[ParameterCode.UserId];
02679                                 if (!string.IsNullOrEmpty(incomingId))
02680                                 {
02681                                     this.UserId = incomingId;
02682                                     this.LocalPlayer.UserId = incomingId;
02683                                     this.DebugReturn(DebugLevel.INFO, string.Format("Received your UserID from server. Updating local value to: {0}", this.UserId));
02684                                 }
02685                             }
02686                             if (operationResponse.Parameters.ContainsKey(ParameterCode.NickName))
02687                             {
02688                                 this.NickName = (string)operationResponse.Parameters[ParameterCode.NickName];
02689                                 this.DebugReturn(DebugLevel.INFO, string.Format("Received your NickName from server. Updating local value to: {0}", this.NickName));
02690                             }
02691 
02692                             if (operationResponse.Parameters.ContainsKey(ParameterCode.EncryptionData))
02693                             {
02694                                 this.SetupEncryption((Dictionary<byte, object>)operationResponse.Parameters[ParameterCode.EncryptionData]);
02695                             }
02696                         }
02697 
02698                         if (this.Server == ServerConnection.NameServer)
02699                         {
02700                             string receivedCluster = operationResponse[ParameterCode.Cluster] as string;
02701                             if (!string.IsNullOrEmpty(receivedCluster))
02702                             {
02703                                 this.CurrentCluster = receivedCluster;
02704                             }
02705 
02706                             // on the NameServer, authenticate returns the MasterServer address for a region and we hop off to there
02707                             this.MasterServerAddress = operationResponse[ParameterCode.Address] as string;
02708                             if (this.ServerPortOverrides.MasterServerPort != 0)
02709                             {
02710                                 //Debug.LogWarning("Incoming MasterServer Address: "+this.MasterServerAddress);
02711                                 this.MasterServerAddress = ReplacePortWithAlternative(this.MasterServerAddress, this.ServerPortOverrides.MasterServerPort);
02712                                 //Debug.LogWarning("New MasterServer Address: "+this.MasterServerAddress);
02713                             }
02714 
02715                             if (this.AuthMode == AuthModeOption.AuthOnceWss && this.ExpectedProtocol != null)
02716                             {
02717                                 this.DebugReturn(DebugLevel.INFO, string.Format("AuthOnceWss mode. Auth response switches TransportProtocol to ExpectedProtocol: {0}.", this.ExpectedProtocol));
02718                                 this.LoadBalancingPeer.TransportProtocol = (ConnectionProtocol)this.ExpectedProtocol;
02719                                 this.ExpectedProtocol = null;
02720                             }
02721                             this.DisconnectToReconnect();
02722                         }
02723                         else if (this.Server == ServerConnection.MasterServer)
02724                         {
02725                             this.State = ClientState.ConnectedToMasterServer;
02726                             if (this.failedRoomEntryOperation == null)
02727                             {
02728                                 this.ConnectionCallbackTargets.OnConnectedToMaster();
02729                             }
02730                             else
02731                             {
02732                                 this.CallbackRoomEnterFailed(this.failedRoomEntryOperation);
02733                                 this.failedRoomEntryOperation = null;
02734                             }
02735 
02736                             if (this.AuthMode != AuthModeOption.Auth)
02737                             {
02738                                 this.LoadBalancingPeer.OpSettings(this.EnableLobbyStatistics);
02739                             }
02740                         }
02741                         else if (this.Server == ServerConnection.GameServer)
02742                         {
02743                             this.State = ClientState.Joining;
02744 
02745                             if (this.enterRoomParamsCache.JoinMode == JoinMode.RejoinOnly)
02746                             {
02747                                 this.enterRoomParamsCache.PlayerProperties = null;
02748                             }
02749                             else
02750                             {
02751                                 Hashtable allProps = new Hashtable();
02752                                 allProps.Merge(this.LocalPlayer.CustomProperties);
02753 
02754                                 if (!string.IsNullOrEmpty(this.LocalPlayer.NickName))
02755                                 {
02756                                     allProps[ActorProperties.PlayerName] = this.LocalPlayer.NickName;
02757                                 }
02758 
02759                                 this.enterRoomParamsCache.PlayerProperties = allProps;
02760                             }
02761 
02762                             this.enterRoomParamsCache.OnGameServer = true;
02763 
02764                             if (this.lastJoinType == JoinType.JoinRoom || this.lastJoinType == JoinType.JoinRandomRoom  || this.lastJoinType == JoinType.JoinRandomOrCreateRoom || this.lastJoinType == JoinType.JoinOrCreateRoom)
02765                             {
02766                                 this.LoadBalancingPeer.OpJoinRoom(this.enterRoomParamsCache);
02767                             }
02768                             else if (this.lastJoinType == JoinType.CreateRoom)
02769                             {
02770                                 this.LoadBalancingPeer.OpCreateRoom(this.enterRoomParamsCache);
02771                             }
02772                             break;
02773                         }
02774 
02775                         // optionally, OpAuth may return some data for the client to use. if it's available, call OnCustomAuthenticationResponse
02776                         Dictionary<string, object> data = (Dictionary<string, object>)operationResponse[ParameterCode.Data];
02777                         if (data != null)
02778                         {
02779                             this.ConnectionCallbackTargets.OnCustomAuthenticationResponse(data);
02780                         }
02781                         break;
02782                     }
02783 
02784                 case OperationCode.GetRegions:
02785                     // Debug.Log("GetRegions returned: " + operationResponse.ToStringFull());
02786 
02787                     if (operationResponse.ReturnCode == ErrorCode.InvalidAuthentication)
02788                     {
02789                         this.DebugReturn(DebugLevel.ERROR, string.Format("GetRegions failed. AppId is unknown on the (cloud) server. "+operationResponse.DebugMessage));
02790                         this.Disconnect(DisconnectCause.InvalidAuthentication);
02791                         break;
02792                     }
02793                     if (operationResponse.ReturnCode != ErrorCode.Ok)
02794                     {
02795                         this.DebugReturn(DebugLevel.ERROR, "GetRegions failed. Can't provide regions list. ReturnCode: " + operationResponse.ReturnCode + ": " + operationResponse.DebugMessage);
02796                         this.Disconnect(DisconnectCause.InvalidAuthentication);
02797                         break;
02798                     }
02799                     if (this.RegionHandler == null)
02800                     {
02801                         this.RegionHandler = new RegionHandler(this.ServerPortOverrides.MasterServerPort);
02802                     }
02803 
02804                     if (this.RegionHandler.IsPinging)
02805                     {
02806                         this.DebugReturn(DebugLevel.WARNING, "Received an response for OpGetRegions while the RegionHandler is pinging regions already. Skipping this response in favor of completing the current region-pinging.");
02807                         return; // in this particular case, we suppress the duplicate GetRegion response. we don't want a callback for this, cause there is a warning already.
02808                     }
02809 
02810                     this.RegionHandler.SetRegions(operationResponse);
02811                     this.ConnectionCallbackTargets.OnRegionListReceived(this.RegionHandler);
02812 
02813                     if (this.connectToBestRegion)
02814                     {
02815                         // ping minimal regions (if one is known) and connect
02816                         this.RegionHandler.PingMinimumOfRegions(this.OnRegionPingCompleted, this.bestRegionSummaryFromStorage);
02817                     }
02818                     break;
02819 
02820                 case OperationCode.JoinRandomGame:  // this happens only on the master server. on gameserver this is a "regular" join
02821                 case OperationCode.CreateGame:
02822                 case OperationCode.JoinGame:
02823 
02824                     if (operationResponse.ReturnCode != 0)
02825                     {
02826                         if (this.Server == ServerConnection.GameServer)
02827                         {
02828                             this.failedRoomEntryOperation = operationResponse;
02829                             this.DisconnectToReconnect();
02830                         }
02831                         else
02832                         {
02833                             this.State = (this.InLobby) ? ClientState.JoinedLobby : ClientState.ConnectedToMasterServer;
02834                             this.CallbackRoomEnterFailed(operationResponse);
02835                         }
02836                     }
02837                     else
02838                     {
02839                         if (this.Server == ServerConnection.GameServer)
02840                         {
02841                             this.GameEnteredOnGameServer(operationResponse);
02842                         }
02843                         else
02844                         {
02845                             this.GameServerAddress = (string)operationResponse[ParameterCode.Address];
02846                             if (this.ServerPortOverrides.GameServerPort != 0)
02847                             {
02848                                 //Debug.LogWarning("Incoming GameServer Address: " + this.GameServerAddress);
02849                                 this.GameServerAddress = ReplacePortWithAlternative(this.GameServerAddress, this.ServerPortOverrides.GameServerPort);
02850                                 //Debug.LogWarning("New GameServer Address: " + this.GameServerAddress);
02851                             }
02852 
02853                             string roomName = operationResponse[ParameterCode.RoomName] as string;
02854                             if (!string.IsNullOrEmpty(roomName))
02855                             {
02856                                 this.enterRoomParamsCache.RoomName = roomName;
02857                             }
02858 
02859                             this.DisconnectToReconnect();
02860                         }
02861                     }
02862                     break;
02863 
02864                 case OperationCode.GetGameList:
02865                     if (operationResponse.ReturnCode != 0)
02866                     {
02867                         this.DebugReturn(DebugLevel.ERROR, "GetGameList failed: " + operationResponse.ToStringFull());
02868                         break;
02869                     }
02870 
02871                     List<RoomInfo> _RoomInfoList = new List<RoomInfo>();
02872 
02873                     Hashtable games = (Hashtable)operationResponse[ParameterCode.GameList];
02874                     foreach (string gameName in games.Keys)
02875                     {
02876                         _RoomInfoList.Add(new RoomInfo(gameName, (Hashtable)games[gameName]));
02877                     }
02878 
02879                     this.LobbyCallbackTargets.OnRoomListUpdate(_RoomInfoList);
02880                     break;
02881 
02882                 case OperationCode.JoinLobby:
02883                     this.State = ClientState.JoinedLobby;
02884                     this.LobbyCallbackTargets.OnJoinedLobby();
02885                     break;
02886 
02887                 case OperationCode.LeaveLobby:
02888                     this.State = ClientState.ConnectedToMasterServer;
02889                     this.LobbyCallbackTargets.OnLeftLobby();
02890                     break;
02891 
02892                 case OperationCode.Leave:
02893                     this.DisconnectToReconnect();
02894                     break;
02895 
02896                 case OperationCode.FindFriends:
02897                     if (operationResponse.ReturnCode != 0)
02898                     {
02899                         this.DebugReturn(DebugLevel.ERROR, "OpFindFriends failed: " + operationResponse.ToStringFull());
02900                         this.friendListRequested = null;
02901                         break;
02902                     }
02903 
02904                     bool[] onlineList = operationResponse[ParameterCode.FindFriendsResponseOnlineList] as bool[];
02905                     string[] roomList = operationResponse[ParameterCode.FindFriendsResponseRoomIdList] as string[];
02906 
02907                     //if (onlineList == null || roomList == null || this.friendListRequested == null || onlineList.Length != this.friendListRequested.Length)
02908                     //{
02909                     //    // TODO: Check if we should handle this case better / more extensively
02910                     //    this.DebugReturn(DebugLevel.ERROR, "OpFindFriends failed. Some list is not set. OpResponse: " + operationResponse.ToStringFull());
02911                     //    this.friendListRequested = null;
02912                     //    this.isFetchingFriendList = false;
02913                     //    break;
02914                     //}
02915 
02916                     List<FriendInfo> friendList = new List<FriendInfo>(this.friendListRequested.Length);
02917                     for (int index = 0; index < this.friendListRequested.Length; index++)
02918                     {
02919                         FriendInfo friend = new FriendInfo();
02920                         friend.UserId = this.friendListRequested[index];
02921                         friend.Room = roomList[index];
02922                         friend.IsOnline = onlineList[index];
02923                         friendList.Insert(index, friend);
02924                     }
02925 
02926                     this.friendListRequested = null;
02927 
02928                     this.MatchMakingCallbackTargets.OnFriendListUpdate(friendList);
02929                     break;
02930 
02931                 case OperationCode.WebRpc:
02932                     this.WebRpcCallbackTargets.OnWebRpcResponse(operationResponse);
02933                     break;
02934             }
02935 
02936             if (this.OpResponseReceived != null) this.OpResponseReceived(operationResponse);
02937         }
02938 
02943         public virtual void OnStatusChanged(StatusCode statusCode)
02944         {
02945             switch (statusCode)
02946             {
02947                 case StatusCode.Connect:
02948                     if (this.State == ClientState.ConnectingToNameServer)
02949                     {
02950                         if (this.LoadBalancingPeer.DebugOut >= DebugLevel.ALL)
02951                         {
02952                             this.DebugReturn(DebugLevel.ALL, "Connected to nameserver.");
02953                         }
02954 
02955                         this.Server = ServerConnection.NameServer;
02956                         if (this.AuthValues != null)
02957                         {
02958                             this.AuthValues.Token = null; // when connecting to NameServer, invalidate the secret (only)
02959                         }
02960                     }
02961 
02962                     if (this.State == ClientState.ConnectingToGameServer)
02963                     {
02964                         if (this.LoadBalancingPeer.DebugOut >= DebugLevel.ALL)
02965                         {
02966                             this.DebugReturn(DebugLevel.ALL, "Connected to gameserver.");
02967                         }
02968 
02969                         this.Server = ServerConnection.GameServer;
02970                     }
02971 
02972                     if (this.State == ClientState.ConnectingToMasterServer)
02973                     {
02974                         if (this.LoadBalancingPeer.DebugOut >= DebugLevel.ALL)
02975                         {
02976                             this.DebugReturn(DebugLevel.ALL, "Connected to masterserver.");
02977                         }
02978 
02979                         this.Server = ServerConnection.MasterServer;
02980                         this.ConnectionCallbackTargets.OnConnected(); // if initial connect
02981                     }
02982 
02983 
02984                     if (this.LoadBalancingPeer.TransportProtocol != ConnectionProtocol.WebSocketSecure)
02985                     {
02986                         if (this.Server == ServerConnection.NameServer || this.AuthMode == AuthModeOption.Auth)
02987                         {
02988                             this.LoadBalancingPeer.EstablishEncryption();
02989                         }
02990                     }
02991                     else
02992                     {
02993                         goto case StatusCode.EncryptionEstablished;
02994                     }
02995 
02996                     break;
02997 
02998                 case StatusCode.EncryptionEstablished:
02999                     if (this.Server == ServerConnection.NameServer)
03000                     {
03001                         this.State = ClientState.ConnectedToNameServer;
03002 
03003                         // if there is no specific region to connect to, get available regions from the Name Server. the result triggers next actions in workflow
03004                         if (string.IsNullOrEmpty(this.CloudRegion))
03005                         {
03006                             this.OpGetRegions();
03007                             break;
03008                         }
03009                     }
03010                     else
03011                     {
03012                         // auth AuthOnce, no explicit authentication is needed on Master Server and Game Server. this is done via token, so: break
03013                         if (this.AuthMode == AuthModeOption.AuthOnce || this.AuthMode == AuthModeOption.AuthOnceWss)
03014                         {
03015                             break;
03016                         }
03017                     }
03018 
03019                     // authenticate in all other cases (using the CloudRegion, if available)
03020                     bool authenticating = this.CallAuthenticate();
03021                     if (authenticating)
03022                     {
03023                         this.State = ClientState.Authenticating;
03024                     }
03025                     else
03026                     {
03027                         this.DebugReturn(DebugLevel.ERROR, "OpAuthenticate failed. Check log output and AuthValues. State: " + this.State);
03028                     }
03029                     break;
03030 
03031                 case StatusCode.Disconnect:
03032                     // disconnect due to connection exception is handled below (don't connect to GS or master in that case)
03033                     this.friendListRequested = null;
03034 
03035                     bool wasInRoom = this.CurrentRoom != null;
03036                     this.CurrentRoom = null;    // players get cleaned up inside this, too, except LocalPlayer (which we keep)
03037                     this.ChangeLocalID(-1);     // depends on this.CurrentRoom, so it must be called after updating that
03038 
03039                     if (this.Server == ServerConnection.GameServer && wasInRoom)
03040                     {
03041                         this.MatchMakingCallbackTargets.OnLeftRoom();
03042                     }
03043 
03044                     if (this.ExpectedProtocol != null && this.LoadBalancingPeer.TransportProtocol != this.ExpectedProtocol)
03045                     {
03046                         this.DebugReturn(DebugLevel.INFO, string.Format("On disconnect switches TransportProtocol to ExpectedProtocol: {0}.", this.ExpectedProtocol));
03047                         this.LoadBalancingPeer.TransportProtocol = (ConnectionProtocol)this.ExpectedProtocol;
03048                         this.ExpectedProtocol = null;
03049                     }
03050 
03051                     switch (this.State)
03052                     {
03053                         case ClientState.ConnectWithFallbackProtocol:
03054                             this.EnableProtocolFallback = false;    // the client does a fallback only one time
03055                             this.LoadBalancingPeer.TransportProtocol = (this.LoadBalancingPeer.TransportProtocol == ConnectionProtocol.Tcp) ? ConnectionProtocol.Udp : ConnectionProtocol.Tcp;
03056                             this.NameServerPortInAppSettings = 0;                   // this does not affect the ServerSettings file, just a variable at runtime
03057                             this.ServerPortOverrides = new PhotonPortDefinition();  // use default ports for the fallback
03058 
03059                             if (!this.LoadBalancingPeer.Connect(this.NameServerAddress, this.ProxyServerAddress, this.AppId, this.TokenForInit))
03060                             {
03061                                 return;
03062                             }
03063                             this.State = ClientState.ConnectingToNameServer;
03064                             break;
03065                         case ClientState.PeerCreated:
03066                         case ClientState.Disconnecting:
03067                             if (this.AuthValues != null)
03068                             {
03069                                 this.AuthValues.Token = null; // when leaving the server, invalidate the secret (but not the auth values)
03070                             }
03071                             this.State = ClientState.Disconnected;
03072                             this.ConnectionCallbackTargets.OnDisconnected(this.DisconnectedCause);
03073                             break;
03074 
03075                         case ClientState.DisconnectingFromGameServer:
03076                         case ClientState.DisconnectingFromNameServer:
03077                             this.ConnectToMasterServer();                 // this gets the client back to the Master Server
03078                             break;
03079 
03080                         case ClientState.DisconnectingFromMasterServer:
03081                             this.Connect(this.GameServerAddress, this.ProxyServerAddress, ServerConnection.GameServer);     // this connects the client with the Game Server (when joining/creating a room)
03082                             break;
03083 
03084                         case ClientState.Disconnected:
03085                             // this client is already Disconnected, so no further action is needed.
03086                             // this.DebugReturn(DebugLevel.INFO, "LBC.OnStatusChanged(Disconnect) this.State: " + this.State + ". Server: " + this.Server);
03087                             break;
03088 
03089                         default:
03090                             string stacktrace = "";
03091                             #if DEBUG && !NETFX_CORE
03092                             stacktrace = new System.Diagnostics.StackTrace(true).ToString();
03093                             #endif
03094                             this.DebugReturn(DebugLevel.WARNING, "Got a unexpected Disconnect in LoadBalancingClient State: " + this.State + ". Server: " + this.Server + " Trace: " + stacktrace);
03095 
03096                             if (this.AuthValues != null)
03097                             {
03098                                 this.AuthValues.Token = null; // when leaving the server, invalidate the secret (but not the auth values)
03099                             }
03100                             this.State = ClientState.Disconnected;
03101                             this.ConnectionCallbackTargets.OnDisconnected(this.DisconnectedCause);
03102                             break;
03103                     }
03104                     break;
03105 
03106                 case StatusCode.DisconnectByServerUserLimit:
03107                     this.DebugReturn(DebugLevel.ERROR, "This connection was rejected due to the apps CCU limit.");
03108                     this.DisconnectedCause = DisconnectCause.MaxCcuReached;
03109                     this.State = ClientState.Disconnecting;
03110                     break;
03111                 case StatusCode.DnsExceptionOnConnect:
03112                     this.DisconnectedCause = DisconnectCause.DnsExceptionOnConnect;
03113                     this.State = ClientState.Disconnecting;
03114                     break;
03115                 case StatusCode.ServerAddressInvalid:
03116                     this.DisconnectedCause = DisconnectCause.ServerAddressInvalid;
03117                     this.State = ClientState.Disconnecting;
03118                     break;
03119                 case StatusCode.ExceptionOnConnect:
03120                 case StatusCode.SecurityExceptionOnConnect:
03121                 case StatusCode.EncryptionFailedToEstablish:
03122                     this.DisconnectedCause = DisconnectCause.ExceptionOnConnect;
03123 
03124                     // if enabled, the client can attempt to connect with another networking-protocol to check if that connects
03125                     if (this.EnableProtocolFallback && this.State == ClientState.ConnectingToNameServer)
03126                     {
03127                         this.State = ClientState.ConnectWithFallbackProtocol;
03128                     }
03129                     else
03130                     {
03131                         this.State = ClientState.Disconnecting;
03132                     }
03133                     break;
03134                 case StatusCode.Exception:
03135                 case StatusCode.ExceptionOnReceive:
03136                 case StatusCode.SendError:
03137                     this.DisconnectedCause = DisconnectCause.Exception;
03138                     this.State = ClientState.Disconnecting;
03139                     break;
03140                 case StatusCode.DisconnectByServerTimeout:
03141                     this.DisconnectedCause = DisconnectCause.ServerTimeout;
03142                     this.State = ClientState.Disconnecting;
03143                     break;
03144                 case StatusCode.DisconnectByServerLogic:
03145                     this.DisconnectedCause = DisconnectCause.DisconnectByServerLogic;
03146                     this.State = ClientState.Disconnecting;
03147                     break;
03148                 case StatusCode.DisconnectByServerReasonUnknown:
03149                     this.DisconnectedCause = DisconnectCause.DisconnectByServerReasonUnknown;
03150                     this.State = ClientState.Disconnecting;
03151                     break;
03152                 case StatusCode.TimeoutDisconnect:
03153                     this.DisconnectedCause = DisconnectCause.ClientTimeout;
03154 
03155                     // if enabled, the client can attempt to connect with another networking-protocol to check if that connects
03156                     if (this.EnableProtocolFallback && this.State == ClientState.ConnectingToNameServer)
03157                     {
03158                         this.State = ClientState.ConnectWithFallbackProtocol;
03159                     }
03160                     else
03161                     {
03162                         this.State = ClientState.Disconnecting;
03163                     }
03164                     break;
03165             }
03166         }
03167 
03168 
03173         public virtual void OnEvent(EventData photonEvent)
03174         {
03175             int actorNr = photonEvent.Sender;
03176             Player originatingPlayer = (this.CurrentRoom != null) ? this.CurrentRoom.GetPlayer(actorNr) : null;
03177 
03178             switch (photonEvent.Code)
03179             {
03180                 case EventCode.GameList:
03181                 case EventCode.GameListUpdate:
03182                     List<RoomInfo> _RoomInfoList = new List<RoomInfo>();
03183 
03184                     Hashtable games = (Hashtable)photonEvent[ParameterCode.GameList];
03185                     foreach (string gameName in games.Keys)
03186                     {
03187                         _RoomInfoList.Add(new RoomInfo(gameName, (Hashtable)games[gameName]));
03188                     }
03189 
03190                     this.LobbyCallbackTargets.OnRoomListUpdate(_RoomInfoList);
03191 
03192                     break;
03193 
03194                 case EventCode.Join:
03195                     Hashtable actorProperties = (Hashtable)photonEvent[ParameterCode.PlayerProperties];
03196 
03197                     if (originatingPlayer == null)
03198                     {
03199                         if (actorNr > 0)
03200                         {
03201                             originatingPlayer = this.CreatePlayer(string.Empty, actorNr, false, actorProperties);
03202                             this.CurrentRoom.StorePlayer(originatingPlayer);
03203                         }
03204                     }
03205                     else
03206                     {
03207                         originatingPlayer.InternalCacheProperties(actorProperties);
03208                         originatingPlayer.IsInactive = false;
03209                         originatingPlayer.HasRejoined = actorNr != this.LocalPlayer.ActorNumber;    // event is for non-local player, who is known (by ActorNumber), so it's a returning player
03210                     }
03211 
03212                     if (actorNr == this.LocalPlayer.ActorNumber)
03213                     {
03214                         // in this player's own join event, we get a complete list of players in the room, so check if we know each of the
03215                         int[] actorsInRoom = (int[])photonEvent[ParameterCode.ActorList];
03216                         this.UpdatedActorList(actorsInRoom);
03217 
03218                         // any operation that does a "rejoin" will set this value to true. this can indicate if the local player returns to a room.
03219                         originatingPlayer.HasRejoined = this.enterRoomParamsCache.JoinMode == JoinMode.RejoinOnly;
03220 
03221                         // joinWithCreateOnDemand can turn an OpJoin into creating the room. Then actorNumber is 1 and callback: OnCreatedRoom()
03222                         if (this.lastJoinType == JoinType.CreateRoom || (this.lastJoinType == JoinType.JoinOrCreateRoom && this.LocalPlayer.ActorNumber == 1))
03223                         {
03224                             this.MatchMakingCallbackTargets.OnCreatedRoom();
03225                         }
03226 
03227                         this.MatchMakingCallbackTargets.OnJoinedRoom();
03228                     }
03229                     else
03230                     {
03231                         this.InRoomCallbackTargets.OnPlayerEnteredRoom(originatingPlayer);
03232                     }
03233                     break;
03234 
03235                 case EventCode.Leave:
03236                     if (originatingPlayer != null)
03237                     {
03238                         bool isInactive = false;
03239                         if (photonEvent.Parameters.ContainsKey(ParameterCode.IsInactive))
03240                         {
03241                             isInactive = (bool)photonEvent.Parameters[ParameterCode.IsInactive];
03242                         }
03243 
03244                         if (isInactive)
03245                         {
03246                             originatingPlayer.IsInactive = true;
03247                         }
03248                         else
03249                         {
03250                             originatingPlayer.IsInactive = false;
03251                             this.CurrentRoom.RemovePlayer(actorNr);
03252                         }
03253                     }
03254 
03255                     if (photonEvent.Parameters.ContainsKey(ParameterCode.MasterClientId))
03256                     {
03257                         int newMaster = (int)photonEvent[ParameterCode.MasterClientId];
03258                         if (newMaster != 0)
03259                         {
03260                             this.CurrentRoom.masterClientId = newMaster;
03261                             this.InRoomCallbackTargets.OnMasterClientSwitched(this.CurrentRoom.GetPlayer(newMaster));
03262                         }
03263                     }
03264                     // finally, send notification that a player left
03265                     this.InRoomCallbackTargets.OnPlayerLeftRoom(originatingPlayer);
03266                     break;
03267 
03268                 case EventCode.PropertiesChanged:
03269                     // whenever properties are sent in-room, they can be broadcasted as event (which we handle here)
03270                     // we get PLAYERproperties if actorNr > 0 or ROOMproperties if actorNumber is not set or 0
03271                     int targetActorNr = 0;
03272                     if (photonEvent.Parameters.ContainsKey(ParameterCode.TargetActorNr))
03273                     {
03274                         targetActorNr = (int)photonEvent[ParameterCode.TargetActorNr];
03275                     }
03276 
03277                     Hashtable gameProperties = null;
03278                     Hashtable actorProps = null;
03279                     if (targetActorNr == 0)
03280                     {
03281                         gameProperties = (Hashtable)photonEvent[ParameterCode.Properties];
03282                     }
03283                     else
03284                     {
03285                         actorProps = (Hashtable)photonEvent[ParameterCode.Properties];
03286                     }
03287 
03288                     this.ReadoutProperties(gameProperties, actorProps, targetActorNr);
03289                     break;
03290 
03291                 case EventCode.AppStats:
03292                     // only the master server sends these in (1 minute) intervals
03293                     this.PlayersInRoomsCount = (int)photonEvent[ParameterCode.PeerCount];
03294                     this.RoomsCount = (int)photonEvent[ParameterCode.GameCount];
03295                     this.PlayersOnMasterCount = (int)photonEvent[ParameterCode.MasterPeerCount];
03296                     break;
03297 
03298                 case EventCode.LobbyStats:
03299                     string[] names = photonEvent[ParameterCode.LobbyName] as string[];
03300                     int[] peers = photonEvent[ParameterCode.PeerCount] as int[];
03301                     int[] rooms = photonEvent[ParameterCode.GameCount] as int[];
03302 
03303                     byte[] types;
03304                     ByteArraySlice slice = photonEvent[ParameterCode.LobbyType] as ByteArraySlice;
03305                     bool useByteArraySlice = slice != null;
03306 
03307                     if (useByteArraySlice)
03308                     {
03309                         types = slice.Buffer;
03310                     }
03311                     else
03312                     {
03313                         types = photonEvent[ParameterCode.LobbyType] as byte[];
03314                     }
03315 
03316                     this.lobbyStatistics.Clear();
03317                     for (int i = 0; i < names.Length; i++)
03318                     {
03319                         TypedLobbyInfo info = new TypedLobbyInfo();
03320                         info.Name = names[i];
03321                         info.Type = (LobbyType)types[i];
03322                         info.PlayerCount = peers[i];
03323                         info.RoomCount = rooms[i];
03324 
03325                         this.lobbyStatistics.Add(info);
03326                     }
03327 
03328                     if (useByteArraySlice)
03329                     {
03330                         slice.Release();
03331                     }
03332 
03333                     this.LobbyCallbackTargets.OnLobbyStatisticsUpdate(this.lobbyStatistics);
03334                     break;
03335 
03336                 case EventCode.ErrorInfo:
03337                     this.ErrorInfoCallbackTargets.OnErrorInfo(new ErrorInfo(photonEvent));
03338                     break;
03339 
03340                 case EventCode.AuthEvent:
03341                     if (this.AuthValues == null)
03342                     {
03343                         this.AuthValues = new AuthenticationValues();
03344                     }
03345 
03346                     this.AuthValues.Token = photonEvent[ParameterCode.Token] as string;
03347                     this.tokenCache = this.AuthValues.Token;
03348                     break;
03349 
03350             }
03351 
03352             this.UpdateCallbackTargets();
03353             if (this.EventReceived != null) this.EventReceived(photonEvent);
03354         }
03355 
03357         public virtual void OnMessage(object message)
03358         {
03359             this.DebugReturn(DebugLevel.ALL, string.Format("got OnMessage {0}", message));
03360         }
