03383         protected internal static string ReplacePortWithAlternative(string address, ushort replacementPort)
03384         {
03385             bool webSocket = address.StartsWith("ws");
03386             if (webSocket)
03387             {
03388                 UriBuilder urib = new UriBuilder(address);
03389                 urib.Port = replacementPort;
03390                 return urib.ToString();
03391             }
03392             else
03393             {
03394                 UriBuilder urib = new UriBuilder(string.Format("scheme://{0}", address));
03395                 return string.Format("{0}:{1}", urib.Host, replacementPort);
03396             }
03397         }
03398 
03399         private void SetupEncryption(Dictionary<byte, object> encryptionData)
03400         {
03401             var mode = (EncryptionMode)(byte)encryptionData[EncryptionDataParameters.Mode];
03402             switch (mode)
03403             {
03404                 case EncryptionMode.PayloadEncryption:
03405                     byte[] encryptionSecret = (byte[])encryptionData[EncryptionDataParameters.Secret1];
03406                     this.LoadBalancingPeer.InitPayloadEncryption(encryptionSecret);
03407                     break;
03408                 case EncryptionMode.DatagramEncryption:
03409                 case EncryptionMode.DatagramEncryptionRandomSequence:
03410                     {
03411                         byte[] secret1 = (byte[])encryptionData[EncryptionDataParameters.Secret1];
03412                         byte[] secret2 = (byte[])encryptionData[EncryptionDataParameters.Secret2];
03413                         this.LoadBalancingPeer.InitDatagramEncryption(secret1, secret2, mode == EncryptionMode.DatagramEncryptionRandomSequence);
03414                     }
03415                     break;
03416                 case EncryptionMode.DatagramEncryptionGCM:
03417                     {
03418                         byte[] secret1 = (byte[])encryptionData[EncryptionDataParameters.Secret1];
03419                         this.LoadBalancingPeer.InitDatagramEncryption(secret1, null, true, true);
03420                     }
03421                     break;
03422                 default:
03423                     throw new ArgumentOutOfRangeException();
03424             }
03425         }
