03428 
03449         public bool OpWebRpc(string uriPath, object parameters, bool sendAuthCookie = false)
03450         {
03451             if (string.IsNullOrEmpty(uriPath))
03452             {
03453                 this.DebugReturn(DebugLevel.ERROR, "WebRPC method name must not be null nor empty.");
03454                 return false;
03455             }
03456             if (!this.CheckIfOpCanBeSent(OperationCode.WebRpc, this.Server, "WebRpc"))
03457             {
03458                 return false;
03459             }
03460             Dictionary<byte, object> opParameters = new Dictionary<byte, object>();
03461             opParameters.Add(ParameterCode.UriPath, uriPath);
03462             if (parameters != null)
03463             {
03464                 opParameters.Add(ParameterCode.WebRpcParameters, parameters);
03465             }
03466             if (sendAuthCookie)
03467             {
03468                 opParameters.Add(ParameterCode.EventForward, WebFlags.SendAuthCookieConst);
03469             }
03470 
03471             //return this.LoadBalancingPeer.OpCustom(OperationCode.WebRpc, opParameters, true);
03472             return this.LoadBalancingPeer.SendOperation(OperationCode.WebRpc, opParameters, SendOptions.SendReliable);
03473         }
