03475 
03490         public void AddCallbackTarget(object target)
03491         {
03492             this.callbackTargetChanges.Enqueue(new CallbackTargetChange(target, true));
03493         }
03494 
03509         public void RemoveCallbackTarget(object target)
03510         {
03511             this.callbackTargetChanges.Enqueue(new CallbackTargetChange(target, false));
03512         }
03513 
03514 
03521         protected internal void UpdateCallbackTargets()
03522         {
03523             while (this.callbackTargetChanges.Count > 0)
03524             {
03525                 CallbackTargetChange change = this.callbackTargetChanges.Dequeue();
03526 
03527                 if (change.AddTarget)
03528                 {
03529                     if (this.callbackTargets.Contains(change.Target))
03530                     {
03531                         //Debug.Log("UpdateCallbackTargets skipped adding a target, as the object is already registered. Target: " + change.Target);
03532                         continue;
03533                     }
03534 
03535                     this.callbackTargets.Add(change.Target);
03536                 }
03537                 else
03538                 {
03539                     if (!this.callbackTargets.Contains(change.Target))
03540                     {
03541                         //Debug.Log("UpdateCallbackTargets skipped removing a target, as the object is not registered. Target: " + change.Target);
03542                         continue;
03543                     }
03544 
03545                     this.callbackTargets.Remove(change.Target);
03546                 }
03547 
03548                 this.UpdateCallbackTarget<IInRoomCallbacks>(change, this.InRoomCallbackTargets);
03549                 this.UpdateCallbackTarget<IConnectionCallbacks>(change, this.ConnectionCallbackTargets);
03550                 this.UpdateCallbackTarget<IMatchmakingCallbacks>(change, this.MatchMakingCallbackTargets);
03551                 this.UpdateCallbackTarget<ILobbyCallbacks>(change, this.LobbyCallbackTargets);
03552                 this.UpdateCallbackTarget<IWebRpcCallback>(change, this.WebRpcCallbackTargets);
03553                 this.UpdateCallbackTarget<IErrorInfoCallback>(change, this.ErrorInfoCallbackTargets);
03554 
03555                 IOnEventCallback onEventCallback = change.Target as IOnEventCallback;
03556                 if (onEventCallback != null)
03557                 {
03558                     if (change.AddTarget)
03559                     {
03560                         EventReceived += onEventCallback.OnEvent;
03561                     }
03562                     else
03563                     {
03564                         EventReceived -= onEventCallback.OnEvent;
03565                     }
03566                 }
03567             }
03568         }
03569 
03574         private void UpdateCallbackTarget<T>(CallbackTargetChange change, List<T> container) where T : class
03575         {
03576             T target = change.Target as T;
03577             if (target != null)
03578             {
03579                 if (change.AddTarget)
03580                 {
03581                     container.Add(target);
03582                 }
03583                 else
03584                 {
03585                     container.Remove(target);
03586                 }
03587             }
03588         }
03589     }
