04414     public class ErrorInfo
04415     {
04419         public readonly string Info;
04420 
04421         public ErrorInfo(EventData eventData)
04422         {
04423             this.Info = eventData[ParameterCode.Info] as string;
04424         }
04425 
04426         public override string ToString()
04427         {
04428             return string.Format("ErrorInfo: {0}", this.Info);
04429         }
04430     }
04431 }
