00176         private void RoomOptionsToOpParameters(Dictionary<byte, object> op, RoomOptions roomOptions, bool usePropertiesKey = false)
00177         {
00178             if (roomOptions == null)
00179             {
00180                 roomOptions = new RoomOptions();
00181             }
00182 
00183             Hashtable gameProperties = new Hashtable();
00184             gameProperties[GamePropertyKey.IsOpen] = roomOptions.IsOpen;
00185             gameProperties[GamePropertyKey.IsVisible] = roomOptions.IsVisible;
00186             gameProperties[GamePropertyKey.PropsListedInLobby] = (roomOptions.CustomRoomPropertiesForLobby == null) ? new string[0] : roomOptions.CustomRoomPropertiesForLobby;
00187             gameProperties.MergeStringKeys(roomOptions.CustomRoomProperties);
00188             if (roomOptions.MaxPlayers > 0)
00189             {
00190                 gameProperties[GamePropertyKey.MaxPlayers] = roomOptions.MaxPlayers;
00191             }
00192 
00193             if (!usePropertiesKey)
00194             {
00195                 op[ParameterCode.GameProperties] = gameProperties;  // typically, the key for game props is 248
00196             }
00197             else
00198             {
00199                 op[ParameterCode.Properties] = gameProperties;      // when an op uses 248 as filter, the "create room" props can be set as 251
00200             }
00201 
00202 
00203             int flags = 0;  // a new way to send the room options as bitwise-flags
00204 
00205             if (roomOptions.CleanupCacheOnLeave)
00206             {
00207                 op[ParameterCode.CleanupCacheOnLeave] = true;                   // this defines the server's room settings and logic
00208                 flags = flags | (int)RoomOptionBit.DeleteCacheOnLeave;          // this defines the server's room settings and logic (for servers that support flags)
00209             }
00210             else
00211             {
00212                 op[ParameterCode.CleanupCacheOnLeave] = false;                  // this defines the server's room settings and logic
00213                 gameProperties[GamePropertyKey.CleanupCacheOnLeave] = false;    // this is only informational for the clients which join
00214             }
00215 
00216             #if SERVERSDK
00217             op[ParameterCode.CheckUserOnJoin] = roomOptions.CheckUserOnJoin;
00218             if (roomOptions.CheckUserOnJoin)
00219             {
00220                 flags = flags | (int) RoomOptionBit.CheckUserOnJoin;
00221             }
00222             #else
00223             // in PUN v1.88 and PUN 2, CheckUserOnJoin is set by default:
00224             flags = flags | (int) RoomOptionBit.CheckUserOnJoin;
00225             op[ParameterCode.CheckUserOnJoin] = true;
00226             #endif
00227 
00228             if (roomOptions.PlayerTtl > 0 || roomOptions.PlayerTtl == -1)
00229             {
00230                 op[ParameterCode.PlayerTTL] = roomOptions.PlayerTtl;    // TURNBASED
00231             }
00232 
00233             if (roomOptions.EmptyRoomTtl > 0)
00234             {
00235                 op[ParameterCode.EmptyRoomTTL] = roomOptions.EmptyRoomTtl;   //TURNBASED
00236             }
00237 
00238             if (roomOptions.SuppressRoomEvents)
00239             {
00240                 flags = flags | (int)RoomOptionBit.SuppressRoomEvents;
00241                 op[ParameterCode.SuppressRoomEvents] = true;
00242             }
00243             if (roomOptions.SuppressPlayerInfo)
00244             {
00245                 flags = flags | (int)RoomOptionBit.SuppressPlayerInfo;
00246             }
00247 
00248             if (roomOptions.Plugins != null)
00249             {
00250                 op[ParameterCode.Plugins] = roomOptions.Plugins;
00251             }
00252             if (roomOptions.PublishUserId)
00253             {
00254                 flags = flags | (int)RoomOptionBit.PublishUserId;
00255                 op[ParameterCode.PublishUserId] = true;
00256             }
00257             if (roomOptions.DeleteNullProperties)
00258             {
00259                 flags = flags | (int)RoomOptionBit.DeleteNullProps; // this is only settable as flag
00260             }
00261             if (roomOptions.BroadcastPropsChangeToAll)
00262             {
00263                 flags = flags | (int)RoomOptionBit.BroadcastPropsChangeToAll; // this is only settable as flag
00264             }
00265 
00266             op[ParameterCode.RoomOptionFlags] = flags;
00267         }
