00349                     op[ParameterCode.LobbyType] = (byte)opParams.Lobby.Type;
00350                 }
00351             }
00352             else if (opParams.JoinMode == JoinMode.RejoinOnly)
00353             {
00354                 op[ParameterCode.JoinMode] = (byte)JoinMode.RejoinOnly; // changed from JoinMode.JoinOrRejoin
00355             }
00356 
00357             if (opParams.ExpectedUsers != null && opParams.ExpectedUsers.Length > 0)
00358             {
00359                 op[ParameterCode.Add] = opParams.ExpectedUsers;
00360             }
00361 
00362             if (opParams.OnGameServer)
00363             {
00364                 if (opParams.PlayerProperties != null && opParams.PlayerProperties.Count > 0)
00365                 {
00366                     op[ParameterCode.PlayerProperties] = opParams.PlayerProperties;
00367                 }
00368                 op[ParameterCode.Broadcast] = true; // broadcast actor properties
00369             }
00370 
00371             if (opParams.OnGameServer || opParams.JoinMode == JoinMode.CreateIfNotExists)
00372             {
00373                 this.RoomOptionsToOpParameters(op, opParams.RoomOptions);
