00737             if (getLobbyStatistics)
00738             {
00739                 // must be sent in operation, even if a Token is available
00740                 opParameters[ParameterCode.LobbyStats] = true;
00741             }
00742 
00743             // shortcut, if we have a Token
00744             if (authValues != null && authValues.Token != null)
00745             {
00746                 opParameters[ParameterCode.Token] = authValues.Token;
00747                 return this.SendOperation(OperationCode.Authenticate, opParameters, SendOptions.SendReliable); // we don't have to encrypt, when we have a token (which is encrypted)
00748             }
00749 
00750 
00751             // without a token, we send a complete op auth
00752 
00753             opParameters[ParameterCode.AppVersion] = appVersion;
00754             opParameters[ParameterCode.ApplicationId] = appId;
00755 
00756             if (!string.IsNullOrEmpty(regionCode))
00757             {
00758                 opParameters[ParameterCode.Region] = regionCode;
00759             }
00760 
00761             if (authValues != null)
00762             {
00763 
00764                 if (!string.IsNullOrEmpty(authValues.UserId))
00765                 {
00766                     opParameters[ParameterCode.UserId] = authValues.UserId;
00767                 }
00768 
00769                 if (authValues.AuthType != CustomAuthenticationType.None)
00770                 {
00771                     opParameters[ParameterCode.ClientAuthenticationType] = (byte)authValues.AuthType;
00772                     // if we had a token, the code above would use it. here, we send parameters:
00773                     if (!string.IsNullOrEmpty(authValues.AuthGetParameters))
00774                     {
00775                         opParameters[ParameterCode.ClientAuthenticationParams] = authValues.AuthGetParameters;
00776                     }
00777                     if (authValues.AuthPostData != null)
00778                     {
00779                         opParameters[ParameterCode.ClientAuthenticationData] = authValues.AuthPostData;
00780                     }
00781                 }
00782             }
00783 
00784             return this.SendOperation(OperationCode.Authenticate, opParameters, new SendOptions() { Reliability = true, Encrypt = true });
00785         }
00786 
00787 
00804         public virtual bool OpAuthenticateOnce(string appId, string appVersion, AuthenticationValues authValues, string regionCode, EncryptionMode encryptionMode, ConnectionProtocol expectedProtocol)
00805         {
00806             if (this.DebugOut >= DebugLevel.INFO)
00807             {
00808                 this.Listener.DebugReturn(DebugLevel.INFO, "OpAuthenticateOnce(): authValues = "  + authValues + ", region = " + regionCode + ", encryption = " + encryptionMode);
00809             }
00810 
00811             var opParameters = new Dictionary<byte, object>();
00812 
00813             // shortcut, if we have a Token
00814             if (authValues != null && authValues.Token != null)
00815             {
00816                 opParameters[ParameterCode.Token] = authValues.Token;
00817                 return this.SendOperation(OperationCode.AuthenticateOnce, opParameters, SendOptions.SendReliable); // we don't have to encrypt, when we have a token (which is encrypted)
00818             }
00819 
00820             if (encryptionMode == EncryptionMode.DatagramEncryption && expectedProtocol != ConnectionProtocol.Udp)
00821             {
00822                 // TODO disconnect?!
00823                 throw new NotSupportedException("Expected protocol set to UDP, due to encryption mode DatagramEncryption.");    // TODO use some other form of callback?!
00824             }
00825 
00826             opParameters[ParameterCode.ExpectedProtocol] = (byte)expectedProtocol;
00827             opParameters[ParameterCode.EncryptionMode] = (byte)encryptionMode;
00828 
00829             opParameters[ParameterCode.AppVersion] = appVersion;
00830             opParameters[ParameterCode.ApplicationId] = appId;
00831 
00832             if (!string.IsNullOrEmpty(regionCode))
00833             {
00834                 opParameters[ParameterCode.Region] = regionCode;
00835             }
00836 
00837             if (authValues != null)
00838             {
00839                 if (!string.IsNullOrEmpty(authValues.UserId))
00840                 {
00841                     opParameters[ParameterCode.UserId] = authValues.UserId;
00842                 }
00843 
00844                 if (authValues.AuthType != CustomAuthenticationType.None)
00845                 {
00846                     opParameters[ParameterCode.ClientAuthenticationType] = (byte)authValues.AuthType;
00847                     if (authValues.Token != null)
00848                     {
00849                         opParameters[ParameterCode.Token] = authValues.Token;
00850                     }
00851                     else
00852                     {
00853                         if (!string.IsNullOrEmpty(authValues.AuthGetParameters))
00854                         {
00855                             opParameters[ParameterCode.ClientAuthenticationParams] = authValues.AuthGetParameters;
00856                         }
00857                         if (authValues.AuthPostData != null)
00858                         {
00859                             opParameters[ParameterCode.ClientAuthenticationData] = authValues.AuthPostData;
00860                         }
00861                     }
00862                 }
00863             }
00864 
00865             return this.SendOperation(OperationCode.AuthenticateOnce, opParameters, new SendOptions() { Reliability = true, Encrypt = true });
00866         }
