00882         public virtual bool OpChangeGroups(byte[] groupsToRemove, byte[] groupsToAdd)
00883         {
00884             if (this.DebugOut >= DebugLevel.ALL)
00885             {
00886                 this.Listener.DebugReturn(DebugLevel.ALL, "OpChangeGroups()");
00887             }
00888 
00889             Dictionary<byte, object> opParameters = new Dictionary<byte, object>();
00890             if (groupsToRemove != null)
00891             {
00892                 opParameters[(byte)ParameterCode.Remove] = groupsToRemove;
00893             }
