00912         public virtual bool OpRaiseEvent(byte eventCode, object customEventContent, RaiseEventOptions raiseEventOptions, SendOptions sendOptions)
00913         {
00914             var paramDict = this.paramDictionaryPool.Acquire();
00915             try
00916             {
00917                 if (raiseEventOptions != null)
00918                 {
00919                     if (raiseEventOptions.CachingOption != EventCaching.DoNotCache)
00920                     {
00921                         paramDict.Add(ParameterCode.Cache, (byte)raiseEventOptions.CachingOption);
00922                     }
00923                     switch (raiseEventOptions.CachingOption)
00924                     {
00925                         case EventCaching.SliceSetIndex:
00926                         case EventCaching.SlicePurgeIndex:
00927                         case EventCaching.SlicePurgeUpToIndex:
00928                             //this.opParameters[(byte) ParameterCode.CacheSliceIndex] =
00929                             //    (byte) raiseEventOptions.CacheSliceIndex;
00930                             return this.SendOperation(OperationCode.RaiseEvent, paramDict, sendOptions);
00931                         case EventCaching.SliceIncreaseIndex:
00932                         case EventCaching.RemoveFromRoomCacheForActorsLeft:
00933                             return this.SendOperation(OperationCode.RaiseEvent, paramDict, sendOptions);
00934                         case EventCaching.RemoveFromRoomCache:
00935                             if (raiseEventOptions.TargetActors != null)
00936                             {
00937                                 paramDict.Add(ParameterCode.ActorList, raiseEventOptions.TargetActors);
00938                             }
00939                             break;
00940                         default:
00941                             if (raiseEventOptions.TargetActors != null)
00942                             {
00943                                 paramDict.Add(ParameterCode.ActorList, raiseEventOptions.TargetActors);
00944                             }
00945                             else if (raiseEventOptions.InterestGroup != 0)
00946                             {
00947                                 paramDict.Add(ParameterCode.Group, (byte)raiseEventOptions.InterestGroup);
00948                             }
00949                             else if (raiseEventOptions.Receivers != ReceiverGroup.Others)
00950                             {
00951                                 paramDict.Add(ParameterCode.ReceiverGroup, (byte)raiseEventOptions.Receivers);
00952                             }
00953                             if (raiseEventOptions.Flags.HttpForward)
00954                             {
00955                                 paramDict.Add(ParameterCode.EventForward, (byte)raiseEventOptions.Flags.WebhookFlags);
00956                             }
00957                             break;
00958                     }
00959                 }
00960                 paramDict.Add(ParameterCode.Code, (byte)eventCode);
00961                 if (customEventContent != null)
00962                 {
00963                     paramDict.Add(ParameterCode.Data, (object)customEventContent);
00964                 }
00965                 return this.SendOperation(OperationCode.RaiseEvent, paramDict, sendOptions);
00966             }
00967             finally
00968             {
00969                 this.paramDictionaryPool.Release(paramDict);
00970             }
00971         }
