00979         public virtual bool OpSettings(bool receiveLobbyStats)
00980         {
00981             if (this.DebugOut >= DebugLevel.ALL)
00982             {
00983                 this.Listener.DebugReturn(DebugLevel.ALL, "OpSettings()");
00984             }
00985 
00986             Dictionary<byte, object> opParameters = new Dictionary<byte, object>();
00987 
00988             // implementation for Master Server:
00989             if (receiveLobbyStats)
00990             {
00991                 opParameters[(byte)0] = receiveLobbyStats;
00992             }
00993 
00994             if (opParameters.Count == 0)
00995             {
00996                 // no need to send op in case we set the default values
00997                 return true;
00998             }
00999 
01000             return this.SendOperation(OperationCode.ServerSettings, opParameters, SendOptions.SendReliable);
01001         }
