01005     internal enum RoomOptionBit : int
01006     {
01007         CheckUserOnJoin = 0x01,  // toggles a check of the UserId when joining (enabling returning to a game)
01008         DeleteCacheOnLeave = 0x02,  // deletes cache on leave
01009         SuppressRoomEvents = 0x04,  // suppresses all room events
01010         PublishUserId = 0x08,  // signals that we should publish userId
01011         DeleteNullProps = 0x10,  // signals that we should remove property if its value was set to null. see RoomOption to Delete Null Properties
01012         BroadcastPropsChangeToAll = 0x20,  // signals that we should send PropertyChanged event to all room players including initiator
01013         SuppressPlayerInfo = 0x40,  // disables events join and leave from the server as well as property broadcasts in a room (to minimize traffic)
01014     }
