01019     public class FindFriendsOptions
01020     {
01022         public bool CreatedOnGs = false;    //flag: 0x01
01024         public bool Visible = false;        //flag: 0x02
01026         public bool Open = false;           //flag: 0x04
01027 
01030         internal int ToIntFlags()
01031         {
01032             int optionFlags = 0;
01033             if (this.CreatedOnGs)
01034             {
01035                 optionFlags = optionFlags | 0x1;
01036             }
01037             if (this.Visible)
01038             {
01039                 optionFlags = optionFlags | 0x2;
01040             }
01041             if (this.Open)
01042             {
01043                 optionFlags = optionFlags | 0x4;
01044             }
01045             return optionFlags;
01046         }
