02129     public class AuthenticationValues
02130     {
02132         private CustomAuthenticationType authType = CustomAuthenticationType.None;
02133 
02136         public CustomAuthenticationType AuthType
02137         {
02138             get { return authType; }
02139             set { authType = value; }
02140         }
02141 
02147         public string AuthGetParameters { get; set; }
02148 
02151         public object AuthPostData { get; private set; }
02152 
02155         public object Token { get; protected internal set; }
02156 
02159         public string UserId { get; set; }
02160 
02161 
02163         public AuthenticationValues()
02164         {
02165         }
02166 
02169         public AuthenticationValues(string userId)
02170         {
02171             this.UserId = userId;
02172         }
02173 
02177         public virtual void SetAuthPostData(string stringData)
02178         {
02179             this.AuthPostData = (string.IsNullOrEmpty(stringData)) ? null : stringData;
02180         }
02181 
02185         public virtual void SetAuthPostData(byte[] byteData)
02186         {
02187             this.AuthPostData = byteData;
02188         }
02189 
02193         public virtual void SetAuthPostData(Dictionary<string, object> dictData)
02194         {
02195             this.AuthPostData = dictData;
02196         }
02197 
02202         public virtual void AddAuthParameter(string key, string value)
02203         {
02204             string ampersand = string.IsNullOrEmpty(this.AuthGetParameters) ? "" : "&";
02205             this.AuthGetParameters = string.Format("{0}{1}{2}={3}", this.AuthGetParameters, ampersand, System.Uri.EscapeDataString(key), System.Uri.EscapeDataString(value));
02206         }
02207 
02212         public override string ToString()
02213         {
02214             return string.Format("AuthenticationValues = AuthType: {0} UserId: {1}{2}{3}{4}",
02215                                  this.AuthType,
02216                                  this.UserId,
02217                                  string.IsNullOrEmpty(this.AuthGetParameters) ? " GetParameters: yes" : "",
02218                                  this.AuthPostData == null ? "" : " PostData: yes",
02219                                  this.Token == null ? "" : " Token: yes");
02220         }
02221 
02227         public AuthenticationValues CopyTo(AuthenticationValues copy)
02228         {
02229             copy.AuthType = this.AuthType;
02230             copy.AuthGetParameters = this.AuthGetParameters;
02231             copy.AuthPostData = this.AuthPostData;
02232             copy.UserId = this.UserId;
02233             return copy;
02234         }
