00280         private bool CheckPlayersReady()
00281         {
00282             if (!PhotonNetwork.IsMasterClient)
00283             {
00284                 return false;
00285             }
00286 
00287             foreach (Player p in PhotonNetwork.PlayerList)
00288             {
00289                 object isPlayerReady;
00290                 if (p.CustomProperties.TryGetValue(AsteroidsGame.PLAYER_READY, out isPlayerReady))
00291                 {
00292                     if (!(bool) isPlayerReady)
00293                     {
00294                         return false;
00295                     }
00296                 }
00297                 else
00298                 {
00299                     return false;
00300                 }
00301             }
00302 
00303             return true;
00304         }
