00331         private void UpdateCachedRoomList(List<RoomInfo> roomList)
00332         {
00333             foreach (RoomInfo info in roomList)
00334             {
00335                 // Remove room from cached room list if it got closed, became invisible or was marked as removed
00336                 if (!info.IsOpen || !info.IsVisible || info.RemovedFromList)
00337                 {
00338                     if (cachedRoomList.ContainsKey(info.Name))
00339                     {
00340                         cachedRoomList.Remove(info.Name);
00341                     }
00342 
00343                     continue;
00344                 }
00345 
00346                 // Update cached room info
00347                 if (cachedRoomList.ContainsKey(info.Name))
00348                 {
00349                     cachedRoomList[info.Name] = info;
00350                 }
00351                 // Add new room info to cache
00352                 else
00353                 {
00354                     cachedRoomList.Add(info.Name, info);
00355                 }
00356             }
00357         }
00358 
00359         private void UpdateRoomListView()
00360         {
00361             foreach (RoomInfo info in cachedRoomList.Values)
00362             {
00363                 GameObject entry = Instantiate(RoomListEntryPrefab);
00364                 entry.transform.SetParent(RoomListContent.transform);
00365                 entry.transform.localScale = Vector3.one;
00366                 entry.GetComponent<RoomListEntry>().Initialize(info.Name, (byte)info.PlayerCount, info.MaxPlayers);
00367 
00368                 roomListEntries.Add(info.Name, entry);
00369             }
00370         }
