00206         public static Matrix Lerp(Matrix target, Matrix start, Matrix end, float t)
00207         {
00208             var crusher = end.crusher;
00209             target.crusher = crusher;
00210 
00211             target.position = Vector3.Lerp(start.position, end.position, t);
00212 
00213             if (crusher != null && crusher.RotCrusher != null)
00214             {
00215                 if (crusher.RotCrusher.TRSType == TRSType.Quaternion)
00216                     target.rotation = Quaternion.Slerp((Quaternion)start.rotation, (Quaternion)end.rotation, t);
00217                 else
00218                 {
00219                     var srot = (Vector3)start.rotation;
00220                     var erot = (Vector3)end.rotation;
00221                     var ydelta = srot.y - erot.y;
00222                     var zdelta = srot.z - erot.z;
00223 
00224 
00225                     Vector3 unfucked = new Vector3(
00226                         erot.x,
00227                         ydelta > 180 ? erot.y + 360 : ydelta < -180 ? erot.y - 360 : erot.y,
00228                         zdelta > 180 ? erot.z + 360 : zdelta < -180 ? erot.z - 360 : erot.z
00229                         );
00230 
00231                     target.rotation = Vector3.Lerp(srot, (Vector3)unfucked, t);
00232                 }
00233             }
00234             else
00235                 target.rotation = end.rotation;
00236 
00237             target.scale = Vector3.Lerp(start.scale, end.scale, t);
00238 
00239             return target;
00240         }
