00189         public MoData SearchMoByID(string targetUniqueID)
00190         {
00191             MoData node = null;
00192             foreach (var m in missions)
00193             {
00194                 node = m.FindMoDataByID(targetUniqueID);
00195                 if (node != null)
00196                     return node;
00197             }
00198             return node;
00199         }
00200         public void SetMoDataProgressionStatus(string targetUniqueID, MoStatus newMoStatus)
00201         {
00202             MoData foundMoData = SearchMoByID(targetUniqueID);
00203             if (foundMoData != null)
00204             {
00205                 if (foundMoData.progress.moStatus == MoStatus.Complete) // can't make completed objectives active again, need to set it back to inactive before becoming active again
00206                 {
00207                     if (newMoStatus == MoStatus.Active)
00208                         return;
00209                 }
00210                 if (foundMoData.progress.moStatus == MoStatus.Unavailable) // can't make unavailable quest suddenly become active,  need to set it back to inactive before becoming active again
00211                 {
00212                     if (newMoStatus == MoStatus.Active)
00213                         return;
00214                 }
00215                 foundMoData.progress.moStatus = newMoStatus;
00216             }
00217         }
