00310         public MoData SetupNode(MoData childNode)
00311         {
00312             if (childNode.progress.moStatus == MoStatus.Inactive)
00313             {
00314                 //childNode.progress.moStatus = MoStatus.Active;
00315                 if (childNode.uniqueId == null)
00316                     Debug.LogError(childNode.title + " Missing UniqueID");
00317                 if (moDataDictionary.ContainsKey(childNode.uniqueId) == false)
00318                 {
00319                     allMoDatasFound.Add(childNode);
00320                     moDataDictionary.Add(childNode.uniqueId, childNode);
00321                 }
00322                  // Attach to UI for Displaying Objective Lists
00323                 //var spawnedObj = PhotonNetwork.InstantiateRoomObject(uiElementPrefab.name, uiLayoutGroups[i].transform.position, Quaternion.identity);
00324                 for (int i = 0; i < uiLayoutGroups.Count; i++)
00325                 {
00326                     var spawnedObj = GameObject.Instantiate(uiElementPrefab, uiLayoutGroups[i].transform);
00327                     MoUIElement newUIElement = spawnedObj.GetComponent<MoUIElement>();
00328                     //newUIElement.titleText.text = objectiveData.subObjectivesData[j].title;
00329                     //newUIElement.titleText.text = .title;
00330                     newUIElement.name = childNode.uniqueId + " | " + newUIElement.name;
00331                     newUIElement.UpdateUIElement(childNode);
00332                     newUIElement.transform.SetParent(uiLayoutGroups[i].transform);
00333                     moUIElements.Add(newUIElement);
00334                 }
