00380             public bool CanTryComplete
00381             {
00382                 get
00383                 {
00384                     bool condition1Check = this.moStatus == MoStatus.Active && this.count <= 0;
00385                     bool condition2Check = !isFailedConditionMet;
00386                     return condition1Check && condition2Check;
00387                 }
00388             }
