00392         [Header("All child objectives have to be completed before this objective can be completed")]
00393         [Header("Child Mission Objectives Data")]
00394         public List<MoData> childMoData;
00395 
00396         public bool IsAllChildObjectivesComplete
00397         {
00398             get
00399             {
00400                 if (childMoData.Count == 0)
00401                     return true;
00402                 //var completedChildObjectives = childMoData.FindAll(x => x.progress.CanTryComplete == true);
00403                 var completedChildObjectives = childMoData.FindAll(x=>x.progress.moStatus == MoStatus.Complete);
00404                 if (completedChildObjectives.Count == childMoData.Count)
00405                 {
00406                     return true;
00407                 }
00408                 return false;
00409             }
00410         }
00411 
00412         public void TryComplete()
00413         {
00414             if (this.progress.CanTryComplete && IsAllChildObjectivesComplete)
00415             {
00416                 progress.moStatus = MoStatus.Complete;
00417                 OnCompletion.Invoke();
00418             }
00419         }
00420         public void TryProgress()
00421         {
00422             if (progress.moStatus == MoStatus.Active)
00423             {
00424                 //if (progress.count > 0)
00425                 {
00426                     progress.count--;
00427                     OnProgression.Invoke();
00428                 }
00429                 TryComplete();
00430             }
00431         }
00432         public void TryProgress(int progressIncrement)
00433         {
00434             if (progress.count > 0)
00435             {
00436                 // invert increment
00437                 progress.count -= progressIncrement;
00438                 // when count reaches 0, the objective is considered allowed to be completed
00439             }
00440             TryComplete();
00441         }
00442     }
