00043         public static MountsManager EstablishMounts(GameObject go)
00044         {
00046             NetObject netObj = go.GetComponent<NetObject>();
00047 
00048             if (!netObj)
00049                 netObj = go.GetComponentInParent<NetObject>();
00050 
00051             if (!netObj)
00052                 netObj = go.transform.root.gameObject.AddComponent<NetObject>();
00053 
00054 #if PUN_2_OR_NEWER
00055             MountsManager mountsLookup = netObj.transform.GetNestedComponentInChildren<MountsManager, NetObject>(true);
00056 #else
00057             MountsManager mountsLookup = null;
00058 #endif
00060             if (mountsLookup && mountsLookup.gameObject != go)
00061             {
00062                 Object.DestroyImmediate(mountsLookup);
00063             }
00064 
00066             if (!mountsLookup)
00067                 mountsLookup = netObj.gameObject.AddComponent<MountsManager>();
00068 
00070             var rootMount = netObj.gameObject.GetComponent<Mount>();
00071             if (rootMount == null)
00072             {
00073                 rootMount = netObj.gameObject.gameObject.AddComponent<Mount>();
00074                 rootMount.mountType = new MountSelector(0);
00075             }
00076             else if (rootMount.mountType.id != 0)
00077             {
00078                 rootMount.mountType.id = 0;
00079             }
00080 
00081             mountsLookup.CollectMounts();
00082 
00083             return mountsLookup;
00084         }
00085 
00086 #endif
