00103         public List<Mount> CollectMounts(bool force = false)
00104         {
00105 
00106             indexedMounts.Clear();
00107             mountIdLookup.Clear();
00108 
00109 #if PUN_2_OR_NEWER
00111             transform.GetNestedComponentsInChildren<Mount, NetObject>(indexedMounts);
00112 #endif
00113 
00114             for (int i = 0, cnt = indexedMounts.Count; i < cnt; ++i)
00115             {
00116                 var mount = indexedMounts[i];
00117                 mount.componentIndex = i;
00118 
00119                 int mountTypeId = mount.mountType.id;
00120                 if (!mountIdLookup.ContainsKey(mountTypeId))
00121                     mountIdLookup.Add(mountTypeId, mount);
00122             }
00123 
00124             return indexedMounts;
00125         }
00126 
