00234 
00236                     int idxmask = 1 << idx;
00237                     bool isAlreadyUsed = (idxmask & usedmask) != 0;
00238                     usedmask |= idxmask;
00239 
00240                     bool isthis = (ReferenceEquals(mount.targetObject, thismount));
00241                     const int CNT_WIDTH = 30;
00242                     const int LABL_PAD = 2;
00243 
00245                     EditorGUILayout.BeginHorizontal(isthis ? mountHiliteStyle : mountNoliteStyle);
