00253                         EditorGUI.LabelField(new Rect(indexrect) { xMin = indexrect.xMin - 3 }, new GUIContent(EditorGUIUtility.FindTexture("CollabError"), "More than one Mount has the same compatible mount selection. Only the first will be used."));
00254                     else
00255                     {
00257                         EditorGUI.BeginDisabledGroup(!isthis);
