00039         public static T GetParentComponent<T>(this Transform t)
00040             where T : Component
00041         {
00042             T found = t.GetComponent<T>();
00043 
00044             if (found)
00045                 return found;
00046 
00047             var par = t.parent;
00048             while (par)
00049             {
00050                 found = par.GetComponent<T>();
00051                 if (found)
00052                     return found;
00053                 par = par.parent;
00054             }
00055             return null;
