00079         public static T GetNestedComponentInChildren<T, NestedT>(this Transform t, bool includeInactive)
00080             where T : class
00081             where NestedT : class
00082         {
00083             // Look for the most obvious check first on the root.
00084             var found = t.GetComponent<T>();
00085             if (!ReferenceEquals(found, null))
00086                 return found;
00087 
00088             // No root found, start testing layer by layer - root is the first layer. Add to queue.
00089             nodesQueue.Clear();
00090             nodesQueue.Enqueue(t);
00091 
00092             while (nodesQueue.Count > 0)
00093             {
00094                 var node = nodesQueue.Dequeue();
00095 
00096                 for (int c = 0, ccnt = node.childCount; c < ccnt; ++c)
00097                 {
00098                     var child = node.GetChild(c);
00099 
00100                     // Ignore branches that are not active
00101                     if (!includeInactive && !child.gameObject.activeSelf)
00102                         continue;
00103 
00104                     // Hit a nested node - don't search this node
00105                     if (!ReferenceEquals(child.GetComponent<NestedT>(), null))
00106                         continue;
00107 
00108                     // see if what we are looking for is on this node
00109                     found = child.GetComponent<T>();
00110 
00111                     // Return if we found what we are looking for
00112                     if (!ReferenceEquals(found, null))
00113                         return found;
00114 
00115                     // Add node to queue for next depth pass since nothing was found on this layer.
00116                     nodesQueue.Enqueue(child);
00117                 }
00118 
00119             }
00120             return found;
00121         }
