00201         public static void GetNestedComponentsInParents<T, NestedT>(this Transform t, List<T> list)
00202             where T : class
00203             where NestedT : class
00204         {
00205 
00206             // Get components on the starting node - this is a given.
00207             t.GetComponents(list);
00208 
00209             // If the starting node has the stop component, we are done.
00210             if (!ReferenceEquals(t.GetComponent<NestedT>(), null))
00211                 return;
00212 
00213             var tnode = t.parent;
00214 
00215             // If there is no parent, we are done.
00216             if (ReferenceEquals(tnode, null))
00217                 return;
00218 
00219             nodeStack.Clear();
00220 
00221             while (true)
00222             {
00223                 // add new parent to stack
00224                 nodeStack.Push(tnode);
00225 
00226                 // if this node has the Stop, we are done recursing up.
00227                 if (!ReferenceEquals(tnode.GetComponent<NestedT>(), null))
00228                     break;
00229 
00230                 // Get the next parent node and add it to the stack
00231                 tnode = tnode.parent;
00232 
00233                 // Stop recursing up if the parent is null
00234                 if (ReferenceEquals(tnode, null))
00235                     break;
00236             }
00237 
00238             if (nodeStack.Count == 0)
00239                 return;
00240 
00241             System.Type type = typeof(T);
00242 
00243             // Acquire the right searchlist from our pool
00244             List<T> searchList;
00245             if (!searchLists.ContainsKey(type))
00246             {
00247                 searchList = new List<T>();
00248                 searchLists.Add(type, searchList);
00249             }
00250             else
00251             {
00252                 searchList = searchLists[type] as List<T>;
00253             }
00254 
00255             // Reverse iterate the nodes found. This produces a GetComponentInParent that starts from the parent Stop down to the provided transform
00256             while (nodeStack.Count > 0)
00257             {
00258                 var node = nodeStack.Pop();
00259 
00260                 node.GetComponents(searchList);
00261                 list.AddRange(searchList);
00262             }
00263         }
