00131         private void FixedUpdate()
00132         {
00134             if (NetObject.activeControlledNetObjs.Count == 0 && NetObject.activeUncontrolledNetObjs.Count == 0)
00135                 return;
00136 
00137             if (!TickEngineSettings.single.enableTickEngine)
00138                 return;
00139 
00141             bool readyToSend = NetMsgSends.ReadyToSend;
00142             if (!readyToSend)
00143             {
00144                 DoubleTime.SnapFixed();
00145                 return;
00146             }
00147 
00148             if (simulationHasRun)
00149                 PostSimulate();
00150 
00151             DoubleTime.SnapFixed();
00152 
00153 #if PUN_2_OR_NEWER
00154 
00158             bool doDispatch = true;
00159 
00160             while (PhotonNetwork.InRoom && PhotonNetwork.IsMessageQueueRunning && doDispatch)
00161             {
00162                 // DispatchIncomingCommands() returns true of it found any command to dispatch (event, result or state change)
00163                 doDispatch = PhotonNetwork.NetworkingClient.LoadBalancingPeer.DispatchIncomingCommands();
00164             }
00165 
00166             rtt = PhotonNetwork.GetPing() * .001f;
00167 #endif
00168 
00169             simulationHasRun = true;
00170         }
