00172         void Update()
00173         {
00175             if (NetObject.activeControlledNetObjs.Count == 0 && NetObject.activeUncontrolledNetObjs.Count == 0)
00176                 return;
00177 
00178             if (!TickEngineSettings.single.enableTickEngine)
00179                 return;
00180 
00181             if (simulationHasRun)
00182                 PostSimulate();
00183 
00184             DoubleTime.SnapUpdate();
00185 
00186             NormTimeSinceFixed = (Time.time - Time.fixedTime) / Time.fixedDeltaTime;
00187 
00188             NetMasterCallbacks.OnPreUpdateCallbacks();
00189 
00190             float t = (Time.time - lastSentTickTime) / (TickEngineSettings.netTickInterval);
00191 
00192             // Interpolate NetObjects
00193             NetObject.NetObjDictsLocked = true;
00194             foreach (var no in NetObject.activeUncontrolledNetObjs.Values)
00195                 no.OnInterpolate(_prevFrameId, _currFrameId, t);
00196             NetObject.NetObjDictsLocked = false;
00197 
00198             // Interpolate Others
00199             NetMasterCallbacks.OnInterpolateCallbacks(_prevFrameId, _currFrameId, t);
00200         }
