00212         void PostSimulate()
00213         {
00214             bool isNetTick = _currSubFrameId == TickEngineSettings.sendEveryXTick - 1;
00215 
00216             NetMasterCallbacks.OnPostSimulateCallbacks(_currFrameId, _currSubFrameId, isNetTick);
00217 
00218             if (isNetTick)
00219                 SerializeAllAndSend();
00220 
00221             IncrementFrameId();
00222 
00223             simulationHasRun = false;
00224         }
